import React, { useState } from "react";
import { Sidebar, Navbar } from "./";
import {
  Box,
  Stack,
  Typography,
  Button,
  CircularProgress,
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  TextField,
} from "@mui/material";
import FileUploadIcon from "@mui/icons-material/FileUpload";
import axios from "axios";
import Cookies from "js-cookie";

const UploadVideo = () => {
  const [selectedCategory, setSelectedCategory] = useState("Upload");
  const [loading, setLoading] = React.useState(false);
  const [heading, setHeading] = React.useState(
    "Drag and drop a file you want to upload"
  );
  const [smallHeading, setSmallHeading] = React.useState(
    "Once uploaded and described, your video will be visible on homepage"
  );
  const [visibility, setVisibility] = useState("private");
  const [selectedFile, setSelectedFile] = useState();
  const [videoTitle, setVideoTitle] = useState("");

  const handleVisibilityChange = (event) => {
    setVisibility(event.target.value);
  };

  const handleTitleChange = (event) => {
    setVideoTitle(event.target.value);
  };

  const handleFileUpload = (e) => {
    if (!e.target.files) {
      return;
    }
    setSelectedFile(e.target.files[0]);
    const token = Cookies.get("jwtToken");
    const formData = new FormData();
    formData.append("jwt", token);
    formData.append("file", e.target.files[0]);
    formData.append("title", videoTitle);

    // Define the API endpoint where you want to send the POST request
    const apiUrl = "http://127.0.0.1:8000/api/upload/";

    axios
      .post(apiUrl, formData, {
        headers: {
          "Content-Type": "multipart/form-data", // Use ‘multipart/form-data’ for file uploads
        },
      })
      .then((response) => {
        // Handle the successful response
        console.log("Response:", response.data);
        setHeading("Your file is being loaded, do not refresh page the page");
        setSmallHeading(
          "You will be redirected to the video page when the video is described"
        );
        setLoading(true);
      })
      .catch((error) => {
        // Handle errors, if any
        console.error("Error:", error);
        setHeading("Error in uploading your file, please try again");
        setSmallHeading("Make sure, you are uploading an mp4 file");
      });
  };

  return (
    <div>
      <Navbar />
      {/*  The box is the sidebar */}
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Stack
          spacing={2}
          paddingTop={15}
          width={"100%"}
          justifyContent={"flex-start"}
          alignItems={"center"}
        >
          <Typography
            variant="h4"
            fontWeight="bold"
            paddingTop={3}
            color="primary.dark"
          >
            {heading}
          </Typography>
          <Typography variant="caption" fontWeight="bold" color="primary.dark">
            {smallHeading}
          </Typography>
          <Typography
            variant="caption"
            fontWeight="bold"
            color="secondary.main"
          >
            Choose whether you would like the video to be public or private. The
            video is private by default.
          </Typography>
          {!loading && (
            <Box>
              <FormControl component="fieldset">
                <RadioGroup
                  row
                  aria-label="visibility"
                  name="visibility"
                  value={visibility}
                  onChange={handleVisibilityChange}
                >
                  <FormControlLabel
                    value="private"
                    control={<Radio />}
                    label="Private"
                  />
                  <FormControlLabel
                    value="public"
                    control={<Radio />}
                    label="Public"
                  />
                </RadioGroup>
              </FormControl>
              <br />
              <TextField
                label="Video Title"
                variant="outlined"
                margin="normal"
                value={videoTitle}
                onChange={handleTitleChange}
              />
              <br />
              <Button
                sx={{ backgroundColor: "secondary.main", color: "white" }}
                className="category-btn"
                component="label"
                startIcon={<FileUploadIcon />}
                disabled={loading}
              >
                Upload Video File
                <input
                  type="file"
                  accept=".mp4"
                  hidden
                  onChange={handleFileUpload}
                />
              </Button>
            </Box>
          )}

          {loading && <CircularProgress />}
        </Stack>

        {/* This is for if the user wants to upload a youtube link */}
      </Stack>
    </div>
  );
};

export default UploadVideo;
