import React, { useState } from "react";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [played, setPlayed] = useState();
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");
  const [selectedFrameIndex, setSelectedFrameIndex] = useState(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);

  const handleAddFrame = () => {
    if (played) {
      const newFrame = { time: played, description: description };
      setFrames([...frames, newFrame]);
      setDescription("");
    }
  };

  const handleEditFrame = (index) => {
    setSelectedFrameIndex(index);
    setDescription(frames[index].description);
    setPlayed(frames[index].time); // Set the timestamp in the input field
    setEditDialogOpen(true);
  };

  const handleSaveEdit = () => {
    const updatedFrames = [...frames];
    updatedFrames[selectedFrameIndex].description = description;
    updatedFrames[selectedFrameIndex].time = played; // Update the timestamp
    setFrames(updatedFrames);
    setEditDialogOpen(false);
  };

  // ... other functions and components

  return (
    <div>
      {/* ... other components */}
      <Dialog
        open={editDialogOpen}
        onClose={() => setEditDialogOpen(false)}
        fullWidth
      >
        <DialogTitle>Edit Description</DialogTitle>
        <DialogContent>
          <TextField
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            variant="outlined"
            fullWidth
            placeholder="Enter description"
            style={{ marginBottom: "10px" }}
          />
          <TextField
            value={played}
            onChange={(e) => setPlayed(e.target.value)}
            variant="outlined"
            fullWidth
            placeholder="Enter timestamp"
            style={{ marginBottom: "10px" }}
          />
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => setEditDialogOpen(false)}
            sx={{ backgroundColor: "secondary.main", color: "white" }}
            className="category-btn"
          >
            Cancel
          </Button>
          <Button
            onClick={handleSaveEdit}
            sx={{ backgroundColor: "secondary.main", color: "white" }}
            className="category-btn"
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AddDescriptions;



