import React, {  useState} from "react";
import { Box, Stack, Tab, Grid} from "@mui/material";
import { TabContext, TabList, TabPanel} from "@mui/lab";
import {Sidebar,  Player} from "./";

const Issues = () => {
    const [selectedCategory, setSelectedCategory] = useState("Home");
    const [value, setValue] = React.useState('1');

    const handleChange = (event: React.SyntheticEvent, newValue: string) => {
        setValue(newValue);
    };

    return (
        // The box is the sidebar
        <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 } }}>
          <Sidebar 
            selectedCategory = {selectedCategory}
            setSelectedCategory = {setSelectedCategory}
          />
        </Box>
        <Grid item xs={12} md={10}>
            <Player/>
        </Grid>

        {/* This container consists of the video player, the video information for now is hardcoded*/}
        <Box md={12}>
            <TabContext value = {value}>
                <Box>
                    <TabList aria-label= 'tabs for AI Q&A and AI description' onChange={handleChange}>
                        <Tab label='Q&A' value='1' />
                        <Tab label='Description' value='2' />

                    </TabList>
                </Box>
                <TabPanel value='1'>
                oanel 1
                </TabPanel>
                <TabPanel value='2'>
                    oanel 2
                </TabPanel>
            </TabContext>
        </Box>
        </Stack>
  )
}

export default Issues
