import React, {  useState, useRef } from "react";
import { Box, Stack, Typography, Grid, Button} from "@mui/material";
import {Sidebar, Comment, Player} from "./";
import Comments from "../data/comments";

const VideoPage = () => {
    const [selectedCategory, setSelectedCategory] = useState("Home");
    const videoRef = useRef(null);

  return (
    // The box is the sidebar
    <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
    <Box sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 } }}>
      <Sidebar 
        selectedCategory = {selectedCategory}
        setSelectedCategory = {setSelectedCategory}
      />
    </Box>

    {/* This container consists of the video player, the video information for now is hardcoded*/}
    <Grid container spacing={2} paddingTop={2} paddingRight={2}>
        <Grid item xs={12} md={8}>
            <Player/>
            <Typography variant="h6"  sx={{color : "#1D5B79"}}> 
              "Simple everyday life: Daily vlog - Mushrooms omelette, Korean potatoes side dish, mushrooms rice" 
            </Typography>
            <Typography variant="subtitle2"  sx={{color : "#468B97"}}> 
              Video Author: Ashley Dunhill            
            </Typography>
            <hr/>
        </Grid>

        {/* This grid represent the comments asked info, showing this next to the video */}
        <Grid item xs={6} md={4} sx ={{overflow: "scroll", height : { md: '500px' }}}>
            <Box p={2} sx= {{ backgroundColor : "#1D5B79" , borderRadius : "5px"}}>
                <Typography variant="h6" sx={{color : "white"}} >InfoBot Q&A</Typography>
                <hr/>
              <Box sx ={{overflow: "scroll", height : { md: '45vh' }}}>
              {Comments.map((item) => (
                <Comment
                  id = {item.id}
                  username = {item.user}
                  question = {item.question}
                  answer = {item.answer}
                  timeStamp = {item.timestamp}
                  /> 
              ))}
              </Box>
            </Box>
            <Button className="category-btn" >Ask infobot</Button>
        </Grid>
    </Grid>
  </Stack>
  )
}

export default VideoPage
