import React, { useState, useEffect } from "react";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [played, setPlayed] = useState();
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleAddFrame = () => {
    if (played) {
      const newFrame = { time: played, description: description };
      setFrames([...frames, newFrame]);
      setDescription("");
    }
  };

  const handleRemoveFrame = (index) => {
    const updatedFrames = [...frames];
    updatedFrames.splice(index, 1);
    setFrames(updatedFrames);
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Grid container spacing={2} p={3} sx={{ overflowY: "auto", height: "90vh", flex: 2 }}>
          <Grid item xs={12} md={8}>
            <Player path={"videos/19.mp4"} parentCallback={handleCallback} />
          </Grid>

          <Notes videoId={19} />

          <Grid item xs={6} md={4}>
            <Box p={2} sx={{ backgroundColor: "primary.main", borderRadius: "5px", paddingBottom: "20px" }}>
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                Add Frames and Descriptions
              </Typography>
              <hr />
              <TextField
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variant="outlined"
                fullWidth
                placeholder="Enter description"
                sx={{ marginBottom: "10px", backgroundColor:"white" }}
              />
              <Button  sx={{ backgroundColor: "secondary.main", color: "white", marginTop: "10px" }} className="category-btn" onClick={handleAddFrame}>
                Add Frame
              </Button>
            </Box>

            <List>
              {frames.map((frame, index) => (
                <ListItem key={index}>
                  <ListItemText primary={`Time: ${frame.time}`} secondary={`Description: ${frame.description}`} />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="delete" onClick={() => handleRemoveFrame(index)}>
                      Delete
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default AddDescriptions;
