import React, { useState, useEffect } from "react";
import { Typography, Grid, Chip } from "@mui/material";
import axios from "axios";
import Cookies from "js-cookie";

const Comment = (props) => {
  const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
  const token = Cookies.get("jwtToken");
  const [comments, setComments] = useState([]);
  const [isLoading, setLoading] = useState(true);
  // error handling check
  const [noComments, setNoComments] = useState(false);

  useEffect(() => {
    const params = {
      id: props.videoID,
    };

    axios
      .get(apiUrl, {
        params: params,
      })
      .then((response) => {
        setComments(
          response.data.questions.map((item) => {
            return JSON.parse(item);
          })
        );
        if (response.data.question.length > 0){
            setNoComments(true)
        }
        setLoading(false);
        // console.log("Received comments data:", response.data.questions);
      })
      .catch((err) => {
        console.log("Error fetching comments:", err);
        setLoading(false);
      });
  }, []); // Dependency array ensures the effect runs when token changes

  if (isLoading || noComments) {
    return <div className="App">No comments to display</div>;
  }

  return (
    <div>
      {comments.map((comment, index) => (
      <Grid container p={2}>
        <Grid item xs={8} md={10}>
          <Typography variant="subtitle2" sx={{ color: "#468B97" }}>
            @{comment.username}
          </Typography>
          <Typography variant="subtitle2" sx={{ color: "primary.dark" }}>
            Question: {comment.question}
          </Typography>
          <Typography variant="subtitle2" sx={{ color: "primary.dark" }}>
            Answer: {comment.answer}
          </Typography>
        </Grid>
        <Grid item xs={4} md={2}>
          <Chip
            label={comment.time_stamp}
            sx={{
              backgroundColor: "secondary.light",
              color: "white",
              marginBottom: "20px",
            }}
          />
        </Grid>
      </Grid>
      ))}
    </div>
  );
};

export default Comment;
