import React, { useState, useEffect, useRef } from "react";
import { Typography, Box, Grid, Chip } from "@mui/material";
import ReactPlayer from "react-player";
import { TextToSpeech } from ".";

const VideoPlayer = (props) => {
  const video = require("./" + props.path);
  console.log("these are my desc", props.descrip);
  const [videoDescriptions, setVideoDescriptions] = useState(props.descrip);

  const [currentDescriptionIndex, setCurrentDescriptionIndex] = useState(0);
  const [isSpeechPlaying, setIsSpeechPlaying] = useState(false);

  useEffect(() => {
    const video = document.getElementById("video");

    const handleTimeUpdate = () => {
      const currentTime = Math.floor(video.currentTime);
      props.parentCallback(Math.floor(currentTime));
      const index = videoDescriptions.findIndex(
        (description) => parseInt(description.time_stamp) === currentTime
      );
      // console.log("0--->", videoDescriptions[0].time_stamp ,currentTime, parseInt(videoDescriptions[0].time_stamp) === currentTime)

      if (index !== -1 && index !== currentDescriptionIndex) {
        setCurrentDescriptionIndex(index);
        video.pause();
      }
    };

    video.addEventListener("timeupdate", handleTimeUpdate);

    return () => {
      video.removeEventListener("timeupdate", handleTimeUpdate);
    };
  }, [currentDescriptionIndex, videoDescriptions]);

  const handleCallback = () => {
    const video = document.getElementById("video");
    video.play();
  };

  return (
    <div>
      <video id="video" controls width="100%" height="500px">
        <source src={video} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div>
        <Typography variant="h6" color={"primary.dark"} paddingTop={2}>
          {props.title}
        </Typography>
        <hr />
        <Box
          p={2}
          sx={{
            backgroundColor: "#1D5B79",
            borderRadius: "5px",
            marginBottom: "32px",
          }}
        >
          <Box p={1.5} borderRadius="5px" sx={{ backgroundColor: "white" }}>
            <Grid container p={2}>
              <Grid item xs={8} md={10}>
              <Typography variant="body1" sx={{ color: "#1D5B79" }}>
              {videoDescriptions[currentDescriptionIndex].descriptions}
              <hr />
              <TextToSpeech
                text={videoDescriptions[currentDescriptionIndex].descriptions}
                isPlayed={isSpeechPlaying}
                parentCallback={handleCallback}
              />
            </Typography>
              </Grid>
              <Grid item xs={4} md={2}>
                <Chip
                  label={videoDescriptions[currentDescriptionIndex].time_stamp}
                  sx={{
                    backgroundColor: "secondary.light",
                    color: "white",
                    marginBottom: "20px",
                  }}
                />
              </Grid>
            </Grid>
            <Typography variant="body1" sx={{ color: "#1D5B79" }}>
              {videoDescriptions[currentDescriptionIndex].descriptions}
              <hr />
              <TextToSpeech
                text={videoDescriptions[currentDescriptionIndex].descriptions}
                isPlayed={isSpeechPlaying}
                parentCallback={handleCallback}
              />
            </Typography>
          </Box>
        </Box>
      </div>
    </div>
  );
};

export default VideoPlayer;
