import {React , useState, useEffect, useRef} from 'react';
import ReactPlayer from 'react-player';
import { Box, Grid, Typography, Paper } from "@mui/material";
import { TextToSpeech } from "./";

const Player = (props) => {
    const [played, setPlayed] = useState(0);
    const [isPlaying, setIsPlaying] = useState(true);
    const [description, setDescription] = useState(true);
    const video = require('./' + props.path);
    const playerRef = useRef(null);
    //pauses the video once it is played 
    // useEffect(() => {
    //     setIsPlaying(false);
    // }, [isPlaying]);
    function handlePause(desc){
      if (playerRef.current) {
        playerRef.current.pause();
      }
      setDescription(desc)
    }

    return(
    <div>
    <ReactPlayer 
    ref={playerRef}
    url={video}
    playing={isPlaying}
    controls={true} 
    width={"100%"}
    height={"65vh"}
    onProgress={(progress) => {
       setPlayed(Math.floor(progress.playedSeconds));
       props.parentCallback(Math.floor(played/60) + ":" + played%60);
     }}
    />
    <Box
        p={2}
        sx={{
          backgroundColor: "#1D5B79",
          borderRadius: "5px",
          marginBottom: "32px",
        }}
      >
        <Box p={1.5} borderRadius="5px" sx={{ backgroundColor: "white" }}>
          {props.descriptions.map((item) => {
            if (item.time_stamp === (Math.floor(played/60) + ":" + played%60))
            {
              return (
                <Typography variant="body1" sx={{ color: "#1D5B79" }}>
                  {item.descriptions}
                  {handlePause(desc)}
                  <hr />
                  <TextToSpeech text={item.descriptions} />
                </Typography>
              );
          }})}
        </Box>
      </Box>
    </div>
    )
}

export default Player;
