          {props.descriptions.map((item) => {
            if (item.time_stamp === (Math.floor(played/60) + ":" + played%60))
            {
              return (
                <Typography variant="body1" sx={{ color: "#1D5B79" }}>
                  {item.descriptions}
                  {handlePause}
                  <hr />
                  <TextToSpeech text={item.descriptions} />
                </Typography>
              );
          }})}
