import React, { useState } from "react";
import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  Grid,
  Button,
  Typography,
} from "@mui/material";
import StarIcon from "@mui/icons-material/Star";

const fakeUsers = [
  {
    id: 1,
    name: "User 1",
    description: "Description 1",
    rating: 3,
    starClicks: 5,
  },
  {
    id: 2,
    name: "User 2",
    description: "Description 2",
    rating: 4,
    starClicks: 8,
  },
  {
    id: 3,
    name: "User 3",
    description: "Description 3",
    rating: 5,
    starClicks: 12,
  },
];

const ChangeRating = () => {
  const [selectedUser, setSelectedUser] = useState(fakeUsers[1]); // Default selected user
  const [isChangeDescriptionClicked, setIsChangeDescriptionClicked] =
    useState(false);
  const [starClicks, setStarClicks] = useState(selectedUser.starClicks);

  const handleUserChange = (event) => {
    const userId = event.target.value;
    const selected = fakeUsers.find((user) => user.id === userId);
    setSelectedUser(selected);
    setIsChangeDescriptionClicked(false); // Close the change description box when user changes
  };

  const handleStarClick = () => {
    setStarClicks(starClicks + 1); // Increase star clicks
    // You can send the star click data to your API or perform other actions here
  };

  return (
    <Paper
      variant="outlined"
      sx={{ backgroundColor: "primary.main", padding: "20px", width: "50%" }}
    >
      <Grid container alignItems="center" justifyContent="space-between">
        <Grid item>
          <Typography variant="body1">
            Described By: {selectedUser.name}
          </Typography>
          <Typography variant="body2">
            <StarIcon
              color="secondary.main"
              onClick={handleStarClick}
              style={{ cursor: "pointer" }}
            />
            {starClicks} People Rated
          </Typography>
        </Grid>
        <Grid item>
          <Button
            sx={{
              backgroundColor: "secondary.main",
              color: "white",
              width: "100%",
              marginTop: "10px",
            }}
            className="category-btn"
            onClick={() =>
              setIsChangeDescriptionClicked(!isChangeDescriptionClicked)
            }
          >
            Change Description
          </Button>
        </Grid>
      </Grid>
            <hr/>
      {isChangeDescriptionClicked && (
        <Grid container spacing={2} style={{ marginTop: "20px" }}>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel id="user-select-label">Select User</InputLabel>
              <Select
                labelId="user-select-label"
                id="user-select"
                value={selectedUser.id}
                onChange={handleUserChange}
              >
                {fakeUsers.map((user) => (
                  <MenuItem key={user.id} value={user.id}>
                    {user.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
      )}
    </Paper>
  );
};

export default ChangeRating;
