import React from 'react';
import Swal from 'sweetalert2';
import axios from "axios";
import Cookies from "js-cookie";
import { Button } from "@mui/material";
import KeyboardVoiceIcon from '@mui/icons-material/KeyboardVoice';

const AskAI = (props) => {
  const handleAskQuestion = async () => {
    const { value: text } = await Swal.fire({
      input: 'textarea',
      inputLabel: 'Type in your question regarding the frame',
      inputPlaceholder: 'Type your question here...',
      icon: 'question',
      inputAttributes: {
        'aria-label': 'Type the question you want to ask about the frame here'
      },
      showCancelButton: false,
      confirmButtonText: 'Submit Question',
      cancelButtonText: 'Cancel',
    });

    if (text && props.videoID && props.timeStamp) {
      const token = Cookies.get("jwtToken");
      const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
      const postData = {
        "id": props.videoID,
        "question": text,
        "currentTime": props.timeStamp,
        "jwt": token
      };

      try {
        const response = await axios.post(apiUrl, postData);
        console.log("Response:", response.data);
      } catch (error) {
        console.error("Error:", error);
      }
    }
  };

  return (
    <div>
      <Button onClick={handleAskQuestion}>
        <KeyboardVoiceIcon sx={{ fontSize: "70px", backgroundColor:"#1D5B79", color: "#EF6262", ml: "5px", borderRadius:"100%" , padding:"20px", margin: "20px"}} />
      </Button>
    </div>
  );
}

export default AskAI;



