import React, { useState } from "react";
import { Sidebar, Navbar } from "./";
import {
  Box,
  Stack,
  Typography,
  Button,
  CircularProgress,
  TextField,
  InputAdornment,
  IconButton,
  Input,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import FileUploadIcon from "@mui/icons-material/FileUpload";
import axios from "axios";
import Cookies from "js-cookie";

const UploadUrl = () => {
  const [selectedCategory, setSelectedCategory] = useState("Use URL");
  const [loading, setLoading] = React.useState(false);
  const [heading, setHeading] = React.useState(
    "Drag and drop a file you want to upload"
  );
  const [smallHeading, setSmallHeading] = React.useState(
    "Once uploaded and described, your video will be visible on homepage"
  );

  const [url, setUrl] = useState("");

  function handleChange(event) {
    const value = event.target.value;
    console.log("this is my target", event.target.value);
    setUrl(value);
  }

  const handleFileUpload = (e) => {
    const token = Cookies.get("jwtToken");

    // Define the API endpoint where you want to send the POST request
    const apiUrl = "http://127.0.0.1:8000/api/upload/";

    axios
      .post(apiUrl, token)
      .then((response) => {
        // Handle the successful response
        console.log("Response:", response.data);
        setHeading("Your file is being loaded, do not refresh page the page");
        setSmallHeading(
          "You will be redirected to the video page when the video is described"
        );
        setLoading(true);
      })
      .catch((error) => {
        // Handle errors, if any
        console.error("Error:", error);
        setHeading("Error in uploading your file, please try again");
        setSmallHeading("Make sure, you are uploading an mp4 file");
      });
  };

  return (
    <div>
      <Navbar />
      {/*  The box is the sidebar */}
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Stack
          spacing={2}
          paddingTop={15}
          width={"100%"}
          justifyContent={"flex-start"}
          alignItems={"center"}
        >
        <Typography
            variant="h4"
            fontWeight="bold"
            color="primary.dark"
          >
            {heading}
          </Typography>
          <Typography variant="caption" fontWeight="bold" color="primary.dark">
            {smallHeading}
          </Typography>
          <Box paddingTop={3}>
            <TextField
              label="Enter youtube URL"
              fullWidth
              onChange={handleChange}
              value={url}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton edge="end" color="secondary">
                      <SearchIcon />
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          {loading && <CircularProgress />}
        </Stack>

        {/* This is for if the user wants to upload a youtube link */}
      </Stack>
    </div>
  );
};

export default UploadUrl;
