import React, { useState, useEffect } from 'react';
import Speech from 'react-speech-kit';

const VideoPlayer = () => {
  const [videoDescriptions, setVideoDescriptions] = useState([
    "Description for video 1",
    "Description for video 2",
    "Description for video 3",
    // Add more video descriptions as needed
  ]);

  const [currentDescriptionIndex, setCurrentDescriptionIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    // Function to handle video progress and change descriptions
    const handleVideoProgress = () => {
      const video = document.getElementById('video');
      const currentTime = video.currentTime;
      // Logic to change descriptions based on video progress
      if (currentTime >= 5 && currentDescriptionIndex === 0) {
        setCurrentDescriptionIndex(1);
        // Pause the video
        video.pause();
      } else if (currentTime >= 10 && currentDescriptionIndex === 1) {
        setCurrentDescriptionIndex(2);
        // Pause the video
        video.pause();
      }
      // Add more conditions for other time intervals and descriptions as needed
    };

    // Add event listener for video progress
    document.getElementById('video').addEventListener('timeupdate', handleVideoProgress);

    // Clean up event listener on component unmount
    return () => {
      document.getElementById('video').removeEventListener('timeupdate', handleVideoProgress);
    };
  }, [currentDescriptionIndex]);

  return (
    <div>
      <video id="video" controls width="640" height="360">
        <source src="your_video_url.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div>
        <h2>Video Description</h2>
        <p>{videoDescriptions[currentDescriptionIndex]}</p>
      </div>

      <div>
        {isPlaying && (
          <Speech
            text={videoDescriptions[currentDescriptionIndex]}
            voice="Google UK English Female"
            rate="0.9"
            pitch="1"
            onStart={() => console.log('Speech started')}
            onEnd={() => setIsPlaying(false)}
          />
        )}
      </div>
    </div>
  );
};

export default VideoPlayer;



