import React, { useState, useEffect } from "react";
import { Box, Stack, Typography, Grid, Button, Dialog } from "@mui/material";
import {
  Sidebar,
  Comment,
  Player,
  AskAI,
  Navbar,
  TextToSpeech,
  DisplayDescriptions,
} from "./";
import Comments from "../data/comments";
import { useLocation, Link } from "react-router-dom";
import axios from "axios";
import Cookies from "js-cookie";
import { getDescriptions } from "../utils/functions.js";

const VideoPage = () => {
  const [isLoading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [open, setOpen] = React.useState(false);
  const [played, setPlayed] = useState();
  const [desc, setDesc] = useState();
  // If we want to pause the video when the description is played it will need to be handled here
  const [pauseVideo, setPauseVideo] = useState(false);
  // using use location to get the props data along with the link
  const location = useLocation();
  const { video_id } = location.state;

  const [video, setVideo] = useState([]);
  const apiUrl = "http://127.0.0.1:8000/api/video/";
  const token = Cookies.get("jwtToken");

  const params = {
    id: video_id,
    jwt: token,
  };
  const url = "http://127.0.0.1:8000/descriptions/";;
  const parameters = {
    video_id: video_id,
    jwt: token,
  };

  useEffect(() => {
    axios
      .get(apiUrl, {
        params: params,
      })
      .then((response) => {
        setVideo(response.data.video);
        console.log("This is what I am recieving", response.data.video);
      })
      .catch((err) => {
        console.log(err);
      });

    axios
      .get(url, {
        params: parameters,
      })
      .then((response) => {
        console.log("This is what I am recieving", response.data.video);
        setDesc(
          response.data.descriptions.map((item) => {
            return JSON.parse(item);
          })
        );
        setLoading(false);
      })
      .catch((err) => {
        console.log("Not recieving data", err);
      });
  }, []);



  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  // console.log("This is the video", video.video_path);
  console.log("This is what I am recieving in display description", desc);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  // this function just retrives the time the video is played
  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };
  function checkTime(){
    for (let i = 0; i < desc.descriptions.length; i++) {
        if (desc.descriptions[i].time_stamp === played)
        {
            console.log(desc.descriptions[i].time_stamp,desc.descriptions[i].descriptions )
            // setDesc(props.descriptions[i].descriptions)
        }
      }
  }

  setInterval(() => checkTime(), 1000); 

  return (
    // The box is the sidebar
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box sx={{ height: { sx: "auto", md: "100vh" } }}>
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        {/* This container consists of the video player, the video information for now is hardcoded*/}
        <Grid
          container
          spacing={2}
          p={3}
          sx={{ overflowY: "auto", height: "90vh", flex: 2 }}
        >
          <Grid item xs={12} md={8}>
            <Player path={video.video_path} parentCallback={handleCallback} />
            <Typography variant="h6" color={"primary.dark"} paddingTop={2}>
              {video.title}
            </Typography>
            <Typography variant="subtitle2" color={"primary.dark"}>
              {video.title}
            </Typography>
            <hr />
            <TextToSpeech text={"My name is maryam saadat cheema"} />
            <DisplayDescriptions descriptions ={ desc }  timeStamp = {played}/>
          </Grid>

          {/* This grid represent the comments asked info, showing this next to the video */}
          <Grid item xs={6} md={4}>
            <Box
              p={2}
              sx={{
                backgroundColor: "primary.main",
                borderRadius: "5px",
                paddingBottom: "20px",
              }}
            >
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                InfoBot Q&A
              </Typography>
              <hr />
              <Box sx={{ overflow: "scroll", height: { md: "55vh" } }}>
                {Comments.map((item) => (
                  <Comment
                    id={item.id}
                    username={item.user}
                    question={item.question}
                    answer={item.answer}
                    timeStamp={item.timestamp}
                  />
                ))}
              </Box>
            </Box>

            {open && (
              <AskAI
                videoID={video_id}
                timeStamp={played}
                parentCallback={handleClose}
              />
            )}

            <Box
              p={2}
              sx={{
                backgroundColor: "primary.main",
                borderRadius: "5px",
                margin: "20px 0",
              }}
            >
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                InfoBot Q&A
              </Typography>
              <hr />
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                  width: "100%",
                  marginTop: "10px",
                }}
                className="category-btn"
                onClick={handleClickOpen}
              >
                Ask infobot at {played}
              </Button>
              <Link
                to="/EditDescriptionPage"
                state={{ video_id: video_id, video_path: video.video_path }}
              >
                <Button
                  sx={{
                    backgroundColor: "secondary.main",
                    color: "white",
                    width: "100%",
                    marginTop: "10px",
                  }}
                  className="category-btn"
                >
                  Add description
                </Button>
              </Link>
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                  width: "100%",
                  marginTop: "10px",
                }}
                className="category-btn"
              >
                Generate AI description
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default VideoPage;
