import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import axios from 'axios';
// Going to use cookies to store the token
import Cookies from 'js-cookie';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

const apiUrl = 'http://127.0.0.1:8000/login/';

export default function SignIn() {
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
    username: data.get('username'),
      password: data.get('password'),
    });
    const credentials = {
        username: data.get('username'),
        password: data.get('password'),
      };
    // Make the POST request to log in
    axios.post(apiUrl, credentials)
    .then((response) => {
    // Handle a successful login response
    const token = response.data.token; // Assuming the server sends back a JWT token
    // make sure not to delete this console.log
    console.log('Logged in successfully. Token:', token);
    Cookies.set('jwtToken', token, { expires: 3 });
    <Router>
      <Switch>
        <Route path='/' exact element={<Home />} />
      </Switch>
    </Router>
    // Be cautious about security when storing tokens.
    })
    .catch((error) => {
    // Handle login errors, such as incorrect credentials
    console.error('Login failed. Error:', error);
    });
  };

return (
    <Container component="main" maxWidth="xs">
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography variant="h5">
            Sign in
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="username"
              label="Your Username"
              name="username"
              autoComplete="username"
              autoFocus
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              className='category-btn'
              sx={{ mt: 3, mb: 2 , backgroundColor: "secondary.main", color : "white",}}
            >
              Sign In
            </Button>
          </Box>
        </Box>
      </Container>
  );
}
