// This is the the alert box that opens when a user wants to ask a questions regarding a scene of a video
import React from 'react';
import Swal from 'sweetalert2';
import axios from "axios";
import Cookies from "js-cookie";
import {Button,  DialogContent, DialogContentText, DialogActions} from "@mui/material";
import KeyboardVoiceIcon from '@mui/icons-material/KeyboardVoice';

const AskAI = (props) => {
  (async () => {

    const { value: text } = await Swal.fire({
      input: 'textarea',
      inputLabel: 'Type in your question regarding the frame',
      inputPlaceholder: 'Type your question here...',
      icon: 'question',
      inputAttributes: {
        'aria-label': 'Type the question you want to ask about the frame here'
      },
      showCancelButton: false,
      confirmButtonText: 'Submit Question',
      cancelButtonText: 'Cancel',
    })

    if (text) {
      // Swal.fire(text)
      const token = Cookies.get("jwtToken");
      // Define the API endpoint where you want to send the POST request
      const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
      console.log("This is my props id", props.videoID)
      const postData = {
        "id": props.videoID,
        "question":text,
        "currentTime": props.timeStamp,
        "jwt":token
      };

      axios
        .post(apiUrl, postData)
        .then((response) => {
          // Handle the successful response
          console.log("Response:", response.data);

        })
        .catch((error) => {
          // Handle errors, if any
          console.error("Error:", error);
        });
    }

    })()
  return (
    <div>
     { props.parentCallback()}
