import React, {  useState, useEffect } from "react";
import { Box, Stack, Typography } from "@mui/material";
import {Sidebar, Videos, Navbar} from "./";
import axios from 'axios';
import Cookies from 'js-cookie';


const Home = () => {
  const [selectedCategory, setSelectedCategory] = useState("Home");
  const [videoList, setVideoList] = useState([]);

  useEffect(() => {
    loadVideos()
}, [])

  const loadVideos = () => {
    const apiUrl = 'http://127.0.0.1:8000/api/all_videos/';
    const token = Cookies.get('jwtToken');

    const params = {
      "jwt": token,
    };
    console.log("this is the token now",token)
    axios.get(apiUrl, {
      params: params,
    })
      .then((response) => {
        // Handle the successful response
        setVideoList(response.data);
      })
      .catch((error) => {
        // Handle errors, if any
        console.error('Error:', error);
      });
  }
  console.log("these are the videos", videoList.videos[0]);
  // const vid = JSON.parse(videoList.videos)
  // console.log("these are the vid", vid);
  return (
    <div>
    <Navbar/>
    <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
      <Box sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 } }}>
        <Sidebar 
          selectedCategory = {selectedCategory}
          setSelectedCategory = {setSelectedCategory}
        />
      </Box>
      <Box p={2} sx={{ overflowY: "auto", height: "90vh", flex: 2 }}>
        <Typography variant="h4" fontWeight="bold" mb={2} color={"primary.dark"}>
          Recent Descriptions
        </Typography>
        <Videos videos={videoList} />
      </Box>
    </Stack>
    </div>
  );
}

export default Home
