import React, { useState, useEffect } from "react";
import { AlertBar } from "./";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import axios from "axios";
import Cookies from "js-cookie";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import {
  ButtonGroup,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText,
  CircularProgress,
} from "@mui/material";
import KeyboardVoiceIcon from "@mui/icons-material/KeyboardVoice";
import formatTime from "../utils/functions";
import tokenUsable from "../utils/loggedIn";

const AskAI = (props) => {
  const [open, setOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState("");
  const [startOrStop, setStartOrStop] = useState("Stop");
  const [showAlert, setShowAlert] = useState(false); // State to control alert visibility

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === "q" && !open) {
        handleClickOpen();
      }
    };

    document.addEventListener("keydown", handleKeyPress);

    return () => {
      document.removeEventListener("keydown", handleKeyPress);
    };
  }, [open]);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleClickOpen = () => {
    const token = Cookies.get("jwtToken");
    if (!token || !tokenUsable(token)) {
      setShowAlert(true); // Set showAlert to true to show the alert
      return;
    }

    SpeechRecognition.startListening({ continuous: true });
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    SpeechRecognition.stopListening();
    setResponse("");
    resetTranscript();
    setStartOrStop("Stop");
  };

  const handleAnotherQuestion = () => {
    setResponse("");
    resetTranscript();
  };

  const handleReset = () => {
    resetTranscript();
    SpeechRecognition.startListening({ continuous: true });
    setStartOrStop("Stop");
  };

  const handleStartStop = () => {
    if (startOrStop === "Stop") {
      SpeechRecognition.stopListening();
      setStartOrStop("Start");
    } else {
      SpeechRecognition.startListening({ continuous: true });
      setStartOrStop("Stop");
    }
  };

  const handleQuestion = () => {
    setLoading(true);
    const token = Cookies.get("jwtToken");
    const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
    const postData = {
      id: props.videoID,
      question: transcript,
      currentTime: props.timeStamp,
      jwt: token,
    };

    axios
      .post(apiUrl, postData)
      .then((response) => {
        const synth = window.speechSynthesis;
        const newUtterance = new SpeechSynthesisUtterance("Response: " + response.data.answer);
        synth.speak(newUtterance);
        setResponse(response.data.answer);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    // <React.Fragment>

    //   <Button
    //     sx={{
    //       backgroundColor: "secondary.main",
    //       color: "white",
    //       width: "100%",
    //       marginTop: "10px",
    //     }}
    //     className="category-btn"
    //     onClick={handleClickOpen}
    //   >
    //     Ask question at {formatTime(props.timeStamp)}
    //   </Button>
    //   {(loading || response) && (
    //     <Dialog
    //       open={open}
    //       onClose={handleClose}
    //       fullWidth
    //       sx={{ textAlign: "center" }}
    //     >
    //       {/* Dialog content */}
    //     </Dialog>
    //   )}
    //   {!loading && !response && (
    //     <Dialog open={open} onClose={handleClose} sx={{ textAlign: "center" }}>
    //       {/* Dialog content */}
    //     </Dialog>
    //   )}
    // </React.Fragment>
    <>
    {showAlert && <AlertBar render ={showAlert} />} {/* Conditionally render the AlertBar */}
    <React.Fragment>
      <Button
        sx={{
          backgroundColor: "secondary.main",
          color: "white",
          width: "100%",
          marginTop: "10px",
        }}
        className="category-btn"
        onClick={handleClickOpen}
      >
        Ask question at {formatTime(props.timeStamp)}
      </Button>
      {(loading || response) && (
        <Dialog
          open={open}
          onClose={handleClose}
          fullWidth
          sx={{ textAlign: "center" }}
        >
          <KeyboardVoiceIcon
            sx={{
              fontSize: "70px",
              backgroundColor: "#1D5B79",
              color: listening ? "#EF6262" : "white",
              ml: "5px",
              borderRadius: "100%",
              padding: "20px",
              margin: "20px auto",
            }}
          />
          <DialogTitle>
            {!response ? "Retrieving Response" : "Response"}
          </DialogTitle>
          <DialogContent>
            {loading && <CircularProgress />}
            {response && <DialogContentText>Response: {response}</DialogContentText>}
          </DialogContent>
          <DialogActions>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              className="category-btn"
              onClick={handleAnotherQuestion}
            >
              Ask another question
            </Button>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              className="category-btn"
              onClick={handleClose}
            >
              Cancel
            </Button>
          </DialogActions>
        </Dialog>
      )}

      {!loading && !response && (
        <Dialog open={open} onClose={handleClose} sx={{ textAlign: "center" }}>
          <KeyboardVoiceIcon
            sx={{
              fontSize: "70px",
              backgroundColor: "#1D5B79",
              color: listening ? "#EF6262" : "white",
              ml: "5px",
              borderRadius: "100%",
              padding: "20px",
              margin: "20px auto",
            }}
          />
          <DialogTitle>Ask Your Question</DialogTitle>
          <DialogContent>
            <TextField
              id="outlined-textarea"
              label="Your Question"
              placeholder="Your question will be visible here"
              multiline
              value={transcript}
              fullWidth
              disabled
              sx={{ margin: "20px 0" }}
            />
            <ButtonGroup aria-label="Buttons to control speech recognition">
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                }}
                aria-label = "This is a button for start or stopping the speech to text"
                className="category-btn"
                onClick={handleStartStop}
              >
                {startOrStop}
              </Button>
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                }}
                className="category-btn"
                onClick={textToSpeech}
              >
                Text To Speech
              </Button>
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                }}
                className="category-btn"
                onClick={handleReset}
              >
                Reset Question
              </Button>
            </ButtonGroup>
          </DialogContent>
          <DialogActions>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              className="category-btn"
              onClick={handleClose}
            >
              Cancel
            </Button>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              onClick={handleQuestion}
            >
              Submit Question
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </React.Fragment>
    </>
  );
};

export default AskAI;


