import React, { useState, useEffect } from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';

const VideoPlayer = (props) => {
    const video = require('./' + props.path);
  const [videoDescriptions, setVideoDescriptions] = useState([
    "Description for video 1",
    "Description for video 2",
    "Description for video 3",
    // Add more video descriptions as needed
  ]);

  const [currentDescriptionIndex, setCurrentDescriptionIndex] = useState(0);
  const { speak, cancel, speaking } = useSpeechSynthesis();

  useEffect(() => {
    const video = document.getElementById('video');

    const handleVideoPause = () => {
      if (currentDescriptionIndex < videoDescriptions.length) {
        // Pause the video
        video.pause();
        // Create a SpeechSynthesisUtterance object
        const utterance = new SpeechSynthesisUtterance(videoDescriptions[currentDescriptionIndex]);
        // Speak the description
        speak({ utterance });
      }
    };

    // Add event listener for video pause
    video.addEventListener('pause', handleVideoPause);

    // Clean up event listener on component unmount
    return () => {
      video.removeEventListener('pause', handleVideoPause);
      // Cancel speech synthesis when component unmounts
      cancel();
    };
  }, [currentDescriptionIndex, videoDescriptions, speak, cancel]);


  return (
    <div>
      <video id="video" controls width="640" height="360">
        <source src={video} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div>
        <h2>Video Description</h2>
        <p>{videoDescriptions[currentDescriptionIndex]}</p>
      </div>

      <div>
        {speaking && <p>Speaking...</p>}
      </div>
    </div>
  );
};

export default VideoPlayer;



