import React, {  useState } from "react";
import { Box, Stack, Typography, Grid, Paper} from "@mui/material";
import { styled } from '@mui/material/styles';
import {Sidebar, Comment} from "./";
import Comments from "../data/comments";

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

const VideoPage = () => {
    const [selectedCategory, setSelectedCategory] = useState("Home");

  return (
    <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
    <Box sx={{ height: { sx: "auto", md: "92vh" }, paddingRight: { sx: 0, md: 2 } }}>
      <Sidebar 
        selectedCategory = {selectedCategory}
        setSelectedCategory = {setSelectedCategory}
      />
      <Typography className="copyright" variant="body2" sx={{ mt: 1.5, color: "#226597", }}>
        Copyright Â© 2023
      </Typography>
    </Box>
    {/* <Box p={2} sx={{ overflowY: "auto", height: "90vh", flex: 2, backgroundColor : "black"}}>
      <Typography variant="h4" fontWeight="bold" mb={2} sx={{ color: "#226597" }}>
        Hello
      </Typography>
    </Box> */}
    <Grid container spacing={2} paddingTop={2} paddingRight={2}>
        <Grid item xs={12} md={8}>
            <Item>xs=6 md=8</Item>
        </Grid>
        <Grid item xs={6} md={4}>
            <Box p={2} sx= {{ backgroundColor : "#226597" , borderRadius : "5px"}}>
                <Typography variant="h6" sx={{color : "white"}} >InfoBot Q&A</Typography>
                <hr/>
              {comments.map((item, idx) => (
                <Box key={idx}>
                  {<Comments 
                  id = {item.id}
                  url = {item.url}
                  channelTitle = {item.channel}
                  title = {item.title}
                  image = {item.image}
                  /> }
                </Box>
              ))}
            </Box>
        </Grid>
    </Grid>
  </Stack>
  )
}

export default VideoPage
