import React, { useState } from 'react';
import { FormControl, InputLabel, Select, MenuItem, Box, Button } from '@mui/material';
import StarIcon from '@mui/icons-material/Star';

const fakeUsers = [
  { id: 1, name: 'User 1', description: 'Description 1', rating: 3, starClicks: 5 },
  { id: 2, name: 'User 2', description: 'Description 2', rating: 4, starClicks: 8 },
  { id: 3, name: 'User 3', description: 'Description 3', rating: 5, starClicks: 12 },
];

const ChangeRating = () => {
  const [selectedUser, setSelectedUser] = useState(fakeUsers[1]); // Default selected user
  const [isChangeDescriptionClicked, setIsChangeDescriptionClicked] = useState(false);
  const [starClicks, setStarClicks] = useState(selectedUser.starClicks);

  const handleUserChange = (event) => {
    const userId = event.target.value;
    const selected = fakeUsers.find((user) => user.id === userId);
    setSelectedUser(selected);
    setIsChangeDescriptionClicked(false); // Close the change description box when user changes
  };

  const handleStarClick = () => {
    setStarClicks(starClicks + 1); // Increase star clicks
    // You can send the star click data to your API or perform other actions here
  };

  return (
    <div style={{ width: '50%' }}>
      <Box display="flex" alignItems="center" justifyContent="space-between" width={"50%"} sx={{ backgroundColor: "primary.main", borderRadius: "5px"}}>
        <div>
          <p>Described By: {selectedUser.name}</p>
          <StarIcon color="primary" onClick={handleStarClick} style={{ cursor: 'pointer' }} />
          <span>{starClicks} People Rated</span>
        </div>
        <Button variant="outlined" onClick={() => setIsChangeDescriptionClicked(!isChangeDescriptionClicked)}>
          Change Description
        </Button>
      </Box>

      {isChangeDescriptionClicked && (
        <div style={{ marginTop: '20px' }}>
          <FormControl fullWidth>
            <InputLabel id="user-select-label">Select User</InputLabel>
            <Select
              labelId="user-select-label"
              id="user-select"
              value={selectedUser.id}
              onChange={handleUserChange}
            >
              {fakeUsers.map((user) => (
                <MenuItem key={user.id} value={user.id}>
                  {user.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <div style={{ marginTop: '20px' }}>
            <p>Selected Description: {selectedUser.description}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChangeRating;
