import React, {  useState} from "react";
import {Sidebar} from "./";
import { Box, Stack, Typography, Grid} from "@mui/material";
import FileUploadIcon from '@mui/icons-material/FileUpload';
import LoadingButton from '@mui/lab/LoadingButton';

const UploadVideo = () => {
    const [selectedCategory, setSelectedCategory] = useState("Upload");
    const [loading, setLoading] = React.useState(false);
    const [heading, setHeading] = React.useState("Drag and drop a file you want to upload")

    function handleClick() {
        setLoading(true);
        setHeading("Uploading...")
    }

    return (
        // The box is the sidebar
        <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 } }}>
          <Sidebar 
            selectedCategory = {selectedCategory}
            setSelectedCategory = {setSelectedCategory}
          />
        </Box>

        <Grid item spacing={2} p={10}>
            <LoadingButton
                color="secondary"
                onClick={handleClick}
                loading={loading}
                loadingPosition="start"
                startIcon={<FileUploadIcon />}
                variant="contained"
                >
                <span>Upload file</span>
            </LoadingButton>
            <Typography variant="h5" fontWeight="bold" paddingTop={2} sx={{ color: "#226597" }}>
            {heading}
            </Typography>
        </Grid>
        </Stack>)
}

export default UploadVideo
