import React, { useState } from "react";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [played, setPlayed] = useState();
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");
  const [selectedFrameIndex, setSelectedFrameIndex] = useState(null);

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleAddFrame = () => {
    if (played) {
      const newFrame = { time: played, description: description };
      setFrames([...frames, newFrame]);
      setDescription("");
    }
  };

  const handleRemoveFrame = (index) => {
    const updatedFrames = [...frames];
    updatedFrames.splice(index, 1);
    setFrames(updatedFrames);
  };

  const handleEditFrame = (index) => {
    setSelectedFrameIndex(index);
    setDescription(frames[index].description);
  };

  const handleSaveEdit = () => {
    const updatedFrames = [...frames];
    updatedFrames[selectedFrameIndex].description = description;
    setFrames(updatedFrames);
    setSelectedFrameIndex(null);
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        {/* ... (same as before) ... */}

        <Grid item xs={6} md={4}>
          <Box p={2} sx={{ backgroundColor: "primary.light", borderRadius: "5px", paddingBottom: "20px" }}>
            <Typography variant="h6" sx={{ color: "primary.dark" }}>
              Add Frames and Descriptions
            </Typography>
            <hr />
            <TextField
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              variant="outlined"
              fullWidth
              placeholder="Enter description"
              style={{ marginBottom: "10px" }}
            />
            <Button variant="contained" color="primary" onClick={handleAddFrame}>
              Add Frame
            </Button>
          </Box>

          <List>
            {frames.map((frame, index) => (
              <ListItem key={index}>
                {selectedFrameIndex === index ? (
                  <TextField
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    variant="outlined"
                    fullWidth
                    placeholder="Enter description"
                  />
                ) : (
                  <ListItemText primary={`Time: ${frame.time}`} secondary={`Description: ${frame.description}`} />
                )}
                <ListItemSecondaryAction>
                  {selectedFrameIndex === index ? (
                    <IconButton edge="end" aria-label="save" onClick={handleSaveEdit}>
                      Save
                    </IconButton>
                  ) : (
                    <IconButton edge="end" aria-label="edit" onClick={() => handleEditFrame(index)}>
                      Edit
                    </IconButton>
                  )}
                  <IconButton edge="end" aria-label="delete" onClick={() => handleRemoveFrame(index)}>
                    Delete
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </Grid>
    </Stack>
  </div>
  );
};

export default AddDescriptions;
