import * as React from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import axios from "axios";
import Cookies from "js-cookie";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import {
  ButtonGroup,
  DialogTitle,
  DialogContentText,
  CircularProgress,
} from "@mui/material";
import KeyboardVoiceIcon from "@mui/icons-material/KeyboardVoice";
import formatTime from "../utils/functions";

const AskAI = (props) => {
  const [open, setOpen] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [response, setResponse] = React.useState("");

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleQuestion = () => {
    setLoading(true);
    const token = Cookies.get("jwtToken");
    // Define the API endpoint where you want to send the POST request
    const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
    console.log("This is my props id", props.videoID);
    const postData = {
      id: props.videoID,
      question: transcript,
      currentTime: props.timeStamp,
      jwt: token,
    };

    axios
      .post(apiUrl, postData)
      .then((response) => {
        // Handle the successful response
        console.log("Response:", response.data);
        setResponse(response.data.answer);
        setLoading(false);
      })
      .catch((error) => {
        // Handle errors, if any
        console.error("Error:", error);
      });
  };

  return (
    <React.Fragment>
      <Button
        sx={{
          backgroundColor: "secondary.main",
          color: "white",
          width: "100%",
          marginTop: "10px",
        }}
        className="category-btn"
        onClick={handleClickOpen}
      >
        Ask InfoBot at {formatTime(props.timeStamp)}
      </Button>
      {(loading || response) && (
        <Dialog
          open={open}
          onClose={handleClose}
          fullWidth
          sx={{ textAlign: "center" }}
        >
          <KeyboardVoiceIcon
            sx={{
              fontSize: "70px",
              backgroundColor: "#1D5B79",
              color: listening ? "#EF6262" : "white",
              ml: "5px",
              borderRadius: "100%",
              padding: "20px",
              margin: "20px auto",
            }}
          />
          <DialogTitle>
            {!response ? "Retrieving Response" : "Response"}
          </DialogTitle>
          <DialogContent>
            {loading && <CircularProgress />}
            {response && <DialogContentText>{response}</DialogContentText>}
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
          </DialogActions>
        </Dialog>
      )}

      {!loading && !response && (
        <Dialog open={open} onClose={handleClose} sx={{ textAlign: "center" }}>
          <KeyboardVoiceIcon
            sx={{
              fontSize: "70px",
              backgroundColor: "#1D5B79",
              color: listening ? "#EF6262" : "white",
              ml: "5px",
              borderRadius: "100%",
              padding: "20px",
              margin: "20px auto",
            }}
          />
          <DialogTitle>Ask Infobot</DialogTitle>
          <DialogContent>
            <TextField
              id="outlined-textarea"
              label="Your Question"
              placeholder="Your question will be visible here"
              multiline
              value={transcript}
              fullWidth
              disabled
              sx={{ margin: "20px 0" }}
            />
            <ButtonGroup aria-label="Buttons to control speech recognition">
              <Button
                sx={{
                  backgroundColor: "secondary.light",
                  color: "white",
                }}
                className="category-btn"
                onClick={SpeechRecognition.startListening}
              >
                Start
              </Button>
              <Button
                sx={{
                  backgroundColor: "secondary.light",
                  color: "white",
                }}
                className="category-btn"
                onClick={SpeechRecognition.stopListening}
              >
                Stop
              </Button>
              <Button
                sx={{
                  backgroundColor: "secondary.light",
                  color: "white",
                }}
                className="category-btn"
                onClick={resetTranscript}
              >
                Reset Question
              </Button>
            </ButtonGroup>
          </DialogContent>
          <DialogActions>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              className="category-btn"
              onClick={handleClose}
            >
              Cancel
            </Button>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              onClick={handleQuestion}
            >
              Submit Question
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </React.Fragment>
  );
};

export default AskAI;

