import React, {  useState, SyntheticEvent} from "react";
import { Box, Stack, Grid, Tab, Tabs} from "@mui/material";
import {Sidebar,  Player} from "./";

const Issues = () => {
    const [selectedCategory, setSelectedCategory] = useState("Home");
    const [value, setValue] = React.useState(0);

    const handleChange = (event: React.SyntheticEvent, newValue: number) => {
        setValue(newValue);
    };

    return (
        // The box is the sidebar
        <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 } }}>
          <Sidebar 
            selectedCategory = {selectedCategory}
            setSelectedCategory = {setSelectedCategory}
          />
        </Box>

        {/* This container consists of the video player, the video information for now is hardcoded*/}
        <Grid container spacing={2} paddingTop={2} paddingRight={2}>
            <Grid item xs={12} md={10}>
                <Player/>
            </Grid>
            <Tabs
                value={value}
                onChange={handleChange}
                variant="scrollable"
                scrollButtons={false}
                aria-label="scrollable prevent tabs example"
                >
                <Tab label="Item One" />
                <Tab label="Item Two" />
                <Tab label="Item Three" />
                <Tab label="Item Four" />
                <Tab label="Item Five" />
                <Tab label="Item Six" />
                <Tab label="Item Seven" />
            </Tabs>
        </Grid>

        </Stack>
  )
}

export default Issues
