import React, { useState, useEffect } from "react";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";
import formatTime from "../utils/functions";
import { useLocation, Link } from "react-router-dom";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [endTime, setEndTime] = useState("");
  const [played, setPlayed] = useState(0);
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");
  const [selectedFrameIndex, setSelectedFrameIndex] = useState(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);

  const location = useLocation();
  const { video_id, video_path } = location.state;

    // Load frames from localStorage on component mount
    useEffect(() => {
      const storedFrames = localStorage.getItem(`frames_${video_id}`);
      if (storedFrames) {
        setFrames(JSON.parse(storedFrames));
      }
    }, [video_id]);

    // Update frames in localStorage whenever frames state changes
    useEffect(() => {
      localStorage.setItem(`frames_${video_id}`, JSON.stringify(frames));
    }, [frames, video_id]);

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleAddFrame = () => {
    if (description && !isNaN(endTime) && parseInt(endTime) >= 0) {
      const newFrame = {
        start: played,
        end: parseInt(endTime),
        description: description,
      };
      setFrames([...frames, newFrame]);
      setDescription("");
      setEndTime("");
    } else {
      // Handle invalid input (show error message, prevent adding frame, etc.)
      console.log("Invalid input. Please enter a valid end time.");
    }
  };

  const handleRemoveFrame = (index) => {
    const updatedFrames = [...frames];
    updatedFrames.splice(index, 1);
    setFrames(updatedFrames);
  };

  const handleEditFrame = (index) => {
    setSelectedFrameIndex(index);
    setDescription(frames[index].description);
    setPlayed(frames[index].time);
    setEditDialogOpen(true);
  };

  const handleSaveEdit = () => {
    if (description) {
      const updatedFrames = [...frames];
      updatedFrames[selectedFrameIndex].description = description;
      setFrames(updatedFrames);
      setEditDialogOpen(false);
      setDescription("");
      setEndTime("");
    }
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Grid
          container
          spacing={2}
          p={3}
          sx={{ overflowY: "auto", height: "90vh", flex: 2 }}
        >
          <Grid item xs={12} md={8}>
            <Player path={video_path} parentCallback={handleCallback} />
          </Grid>

          <Notes videoId={video_id} />

          <Grid item xs={12} md={8}>
            <Box
              p={2}
              sx={{
                backgroundColor: "primary.main",
                borderRadius: "5px",
                paddingBottom: "20px",
              }}
            >
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                Add description at {formatTime(played)}
              </Typography>
              <hr />
              <TextField
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                variant="outlined"
                type="number"
                placeholder="Enter end time (in seconds)"
                sx={{
                  marginBottom: "10px",
                  backgroundColor: "white",
                  borderRadius: "5px",
                  width: "50%",
                }}
              />
              <TextField
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variant="outlined"
                fullWidth
                placeholder="Enter description"
                sx={{
                  marginBottom: "10px",
                  backgroundColor: "white",
                  borderRadius: "5px",
                }}
              />
              <Button
                sx={{ backgroundColor: "secondary.main", color: "white" }}
                className="category-btn"
                onClick={handleAddFrame}
              >
                Add Description
              </Button>
            </Box>

            {frames
              .slice() // Create a shallow copy of the frames array
              .sort((a, b) => a.start - b.start) // Sort the frames based on starting timestamp
              .map((frame, index) => (
                <Box
                  key={index}
                  p={2}
                  sx={{
                    backgroundColor: "primary.main",
                    borderRadius: "5px",
                    margin: "20px 0",
                  }}
                >
                  <Grid
                    container
                    rowSpacing={2}
                    columnSpacing={{ xs: 1, sm: 2, md: 3 }}
                  >
                    {/* Displays Scene number */}
                    <Grid item xs={2}>
                      <Typography variant="h6" sx={{ color: "primary.dark" }}>
                        Scene {index}
                      </Typography>
                    </Grid>
                    {/* Displays time stamp */}
                    <Grid item xs={2}>
                      <Chip
                        label={
                          formatTime(frame.start) +
                          " - " +
                          formatTime(frame.start + frame.end)
                        }
                        sx={{
                          backgroundColor: "secondary.light",
                          color: "white",
                          marginBottom: "20px",
                        }}
                      />
                    </Grid>
                    <Grid item xs={8}>
                      <Box display="flex" justifyContent="flex-end">
                        <IconButton
                          edge="end"
                          aria-label="delete"
                          onClick={() => handleRemoveFrame(index)}
                        >
                          <DeleteIcon
                            sx={{
                              color: "#EF6262",
                            }}
                            aria-label="delete scene"
                          />
                        </IconButton>
                        <IconButton
                          edge="end"
                          aria-label="edit"
                          onClick={() => handleEditFrame(index)}
                          sx={{ marginLeft: "20px" }}
                        >
                          <EditIcon
                            sx={{
                              color: "#EF6262",
                            }}
                            aria-label="edit scene"
                          />
                        </IconButton>
                      </Box>
                    </Grid>

                    <Grid item xs={3} md={2}>
                      <Typography
                        variant="subtitle1"
                        sx={{ color: "primary.dark" }}
                      >
                        On screen text
                      </Typography>
                    </Grid>
                    <Grid item xs={9} md={10}>
                      <Box
                        p={1.5}
                        borderRadius="5px"
                        sx={{ backgroundColor: "white" }}
                      >
                        <Typography
                          variant="body1"
                          sx={{ color: "primary.dark" }}
                        >
                          Currently, no screen text
                        </Typography>
                      </Box>
                    </Grid>

                    <Grid item xs={3} md={2}>
                      <Typography
                        variant="subtitle1"
                        sx={{ color: "primary.dark" }}
                      >
                        Description
                      </Typography>
                    </Grid>
                    <Grid item xs={9} md={10}>
                      <Box
                        p={1.5}
                        borderRadius="5px"
                        sx={{ backgroundColor: "white" }}
                      >
                        <Typography variant="body1" sx={{ color: "#1D5B79" }}>
                          {frame.description}
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </Box>
              ))}

            <Dialog
              open={editDialogOpen}
              onClose={() => setEditDialogOpen(false)}
              fullWidth
            >
              <DialogTitle>Edit Description at timestamp {played}</DialogTitle>
              <DialogContent>
                <TextField
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  variant="outlined"
                  fullWidth
                  placeholder="Enter description"
                  style={{ marginBottom: "10px" }}
                />
              </DialogContent>
              <DialogActions>
                <Button
                  onClick={() => setEditDialogOpen(false)}
                  sx={{ backgroundColor: "secondary.main", color: "white" }}
                  className="category-btn"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleSaveEdit}
                  sx={{ backgroundColor: "secondary.main", color: "white" }}
                  className="category-btn"
                >
                  Save
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
          {(frames.length > 0) && <Grid item xs={6} md={4}>              
            <Box
              p={2}
              sx={{
                backgroundColor: "primary.main",
                borderRadius: "5px",
              }}
            >
              <Button
                sx={{
                  backgroundColor: "secondary.main",
                  color: "white",
                  width: "100%",
                  marginTop: "10px",
                }}
                className="category-btn"
              // onClick={handleClickOpen}
              >
                Publish Description
              </Button>  
            </Box>
          </Grid>}
        </Grid>
      </Stack>
    </div>
  );
};

export default AddDescriptions;
