import React, { useState, useEffect } from "react";
import { Button } from "@mui/material";

const TextToSpeech = ({ text, isPlayed, onStart, onEnd }) => {
  const [isPaused, setIsPaused] = useState(false);
  // const [utterance, setUtterance] = useState(null);
  const [voice, setVoice] = useState(null);
  const [pitch, setPitch] = useState(1);
  const [rate, setRate] = useState(1);
  const [volume, setVolume] = useState(1);
  const [synth, setSynth] = useState(window.speechSynthesis);
  const [utterance, setUtterance] = useState(new SpeechSynthesisUtterance(text));
  // const [isPaused, setIsPaused] = useState(false);
  useEffect(() => {
    // Set the text content for the utterance whenever the text prop changes
    utterance.text = text;

    // Set up event listeners for speech synthesis events
    utterance.onstart = () => {
      setIsPaused(false);
      if (onStart) {
        onStart();
      }
    };

    utterance.onend = () => {
      setIsPaused(true);
      if (onEnd) {
        onEnd();
      }
    };

    // Handle play, pause, and stop based on the isPlayed prop
    if (isPlayed && !isPaused) {
      synth.speak(utterance);
    } else if (!isPlayed && !synth.paused) {
      synth.pause();
    }

    return () => {
      synth.cancel();
    };
  }, [synth, utterance, text, isPlayed, isPaused, onStart, onEnd]);

  const handlePause = () => {
    const synth = window.speechSynthesis;

    synth.pause();

    setIsPaused(true);
  };

  const handleStop = () => {
    const synth = window.speechSynthesis;

    synth.cancel();

    setIsPaused(false);
  };

  const handleVoiceChange = (event) => {
    const voices = window.speechSynthesis.getVoices();
    setVoice(voices.find((v) => v.name === event.target.value));
  };

  const handleRateChange = (event) => {
    setRate(parseFloat(event.target.value));
  };

  const handleVolumeChange = (event) => {
    setVolume(parseFloat(event.target.value));
  };

  return (
    <div>
      <label>
        Voice:
        <select value={voice?.name} onChange={handleVoiceChange}>
          {window.speechSynthesis.getVoices().map((voice) => (
            <option key={voice.name} value={voice.name}>
              {voice.name}
            </option>
          ))}
        </select>
      </label>

      <br />

      <label>
        Speed:
        <input
          type="range"
          min="0.5"
          max="2"
          step="0.1"
          value={rate}
          onChange={handleRateChange}
        />
      </label>
      <br />
      <label>
        Volume:
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={handleVolumeChange}
        />
      </label>

      <br />

      <Button
        sx={{
          backgroundColor: "secondary.main",
          color: "white",
          margin: "10px",
        }}
        className="category-btn"
        onClick={handlePlay}
      >
        {isPaused ? "Resume" : "Play"}
      </Button>
      <Button
        sx={{
          backgroundColor: "secondary.main",
          color: "white",
          margin: "10px",
        }}
        className="category-btn"
        onClick={handlePause}
      >
        Pause
      </Button>
      <Button
        sx={{
          backgroundColor: "secondary.main",
          color: "white",
          margin: "10px",
        }}
        className="category-btn"
        onClick={handleStop}
      >
        Stop
      </Button>
    </div>
  );
};

export default TextToSpeech;
