import React, { useState } from "react";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [played, setPlayed] = useState();
  const [frames, setFrames] = useState([]);

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleAddFrame = () => {
    if (played) {
      const newFrame = { time: played, description: "" };
      setFrames([...frames, newFrame]);
    }
  };

  const handleSaveDescription = (index, description) => {
    const updatedFrames = [...frames];
    updatedFrames[index].description = description;
    setFrames(updatedFrames);
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Grid container spacing={2} p={3} sx={{ overflowY: "auto", height: "90vh", flex: 2 }}>
          <Grid item xs={12} md={8}>
            <Player path={"videos/19.mp4"} parentCallback={handleCallback} />
          </Grid>

          <Notes videoId={19} />

          <Grid item xs={6} md={4}>
            <Box p={2} sx={{ backgroundColor: "primary.light", borderRadius: "5px", paddingBottom: "20px" }}>
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                Add Frames and Descriptions
              </Typography>
              <hr />
              <Button variant="contained" color="primary" onClick={handleAddFrame}>
                Add Frame
              </Button>

              <List>
                {frames.map((frame, index) => (
                  <ListItem key={index}>
                    <TextField
                      value={frame.description}
                      onChange={(e) => handleSaveDescription(index, e.target.value)}
                      variant="outlined"
                      fullWidth
                      placeholder="Enter description"
                      sx={{ marginRight: 1 }}
                    />
                    <Button variant="contained" color="primary" onClick={() => handleSaveDescription(index, frame.description)}>
                      Save
                    </Button>
                  </ListItem>
                ))}
              </List>
            </Box>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default AddDescriptions;
