import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
  Dialog,
  DialogActions,
  InputLabel,
  FormControl,
  MenuItem,
  Select,
} from '@mui/material';

const fakeUsers = [
  { id: 1, name: 'User 1' },
  { id: 2, name: 'User 2' },
  // Add more users as needed
];

const Scene = () => {
  const [currentUser, setCurrentUser] = useState(fakeUsers[0]);
  const [fakeDescriptions, setFakeDescriptions] = useState([
    { id: 1, timestamp: '00:05', userId: 1, description: 'Description 1 by User 1' },
    { id: 2, timestamp: '00:10', userId: 1, description: 'Description 1 by User 1 at 10 seconds' },
    { id: 3, timestamp: '00:15', userId: 1, description: 'Description 1 by User 1 at 10 seconds' },
    { id: 2, timestamp: '01:30', userId: 2, description: 'Description 2 by User 2' },
    // Add more fake descriptions as needed
  ]);
  const [editedDescription, setEditedDescription] = useState('');
  const [selectedDescriptionId, setSelectedDescriptionId] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const handleUserChange = (event) => {
    const userId = event.target.value;
    const selectedUser = fakeUsers.find((user) => user.id === userId);
    setCurrentUser(selectedUser);
  };

  const handleEditClick = (descriptionId, descriptionText) => {
    setSelectedDescriptionId(descriptionId);
    setEditedDescription(descriptionText);
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    if (selectedDescriptionId !== null) {
      const updatedDescriptions = fakeDescriptions.map((description) =>
        description.id === selectedDescriptionId ? { ...description, description: editedDescription } : description
      );
      setFakeDescriptions(updatedDescriptions);
      setIsEditing(false);
      setSelectedDescriptionId(null);
    }
  };

  return (
    <div>
      <FormControl sx={{ margin: '20px 0', width: "300px", backgroundColor: "primary.main" }}>
        <InputLabel>User</InputLabel>
        <Select value={currentUser.id} onChange={handleUserChange}  sx={{ backgroundColor: "primary.main"}}>
          {fakeUsers.map((user) => (
            <MenuItem key={user.id} value={user.id} >
              {user.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      {fakeDescriptions
        .filter((description) => description.userId === currentUser.id)
        .map((description) => (
          <Card key={description.id} variant="outlined" style={{ marginBottom: '10px' }}>
            <CardContent>
              <Typography variant="h6">Timestamp: {description.timestamp}</Typography>
              <Typography variant="body2">Created by: {currentUser.name}</Typography>
              {isEditing && selectedDescriptionId === description.id ? (
                <TextField
                  multiline
                  fullWidth
                  variant="outlined"
                  label="Edit Description"
                  value={editedDescription}
                  onChange={(e) => setEditedDescription(e.target.value)}
                />
              ) : (
                <Typography variant="body1">{description.description}</Typography>
              )}
              <div style={{ marginTop: '10px' }}>
                {isEditing && selectedDescriptionId === description.id ? (
                  <DialogActions>
                    <Button onClick={handleSaveClick} color="primary">
                      Save
                    </Button>
                  </DialogActions>
                ) : (
                  <Button
                    onClick={() => handleEditClick(description.id, description.description)}
                    color="primary"
                  >
                    Edit
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
    </div>
  );
};

export default Scene;




