import { Box, Stack, Typography, Grid,  TextField, Button} from "@mui/material";
import {Sidebar,  Player, Scene, SceneCards, Navbar} from "./";
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';

const EditDescriptionPage = () => {
    const [selectedCategory, setSelectedCategory] = useState("Home");
    const location = useLocation()
    const {video_id} = location.state
    const [isLoading, setLoading] = useState(true);
    const [played, setPlayed] = useState();
    const [videoDescription, setVideoDescriptions] = useState([]);

    const apiUrl = 'http://127.0.0.1:8000/descriptions/';
    const token = Cookies.get('jwtToken');

      const params = {
        "id": video_id,
        "jwt": token,
      };

    useEffect(() => {
      axios
        .get(apiUrl, {
              params: params,
            })
        .then((response) => {
            setVideo(response.data.video);
            setLoading(false);
            console.log("This is what I am recieving", response.data.video)
        })
        .catch((err) => {
            console.log(err);
        });
  }, []);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }
  // this function just retrives the time the video is played
  const handleCallback = (progressData) =>{
    setPlayed(progressData);
  }


    console.log("this is the video_id", video_id)
  return (
    // The box is the sidebar
    <div>
    <Navbar/>
    <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
    <Box 
    sx={{ height: { sx: "auto", md: "100vh" }, paddingRight: { sx: 0, md: 2 }}}>
      <Sidebar 
        selectedCategory = {selectedCategory}
        setSelectedCategory = {setSelectedCategory}
      />
    </Box>

    {/* This container consists of the video player, the video information for now is hardcoded*/}
    <Grid container spacing={2} paddingTop={2} paddingRight={2}>
        <Grid item xs={12} md={8}>
        <Player path={video.video_path} parentCallback={handleCallback}/>
        </Grid>

        {/* This grid represent the notes section */}
        <Grid item xs={6} md={4}>
            <Box p={2} sx= {{ backgroundColor : "#1D5B79" , borderRadius : "5px", paddingBottom:"20px"}}>
                <Typography variant="h6" sx={{color : "white"}} >Notes</Typography>
                <hr/>
              <Box sx ={{overflow: "scroll", height : { md: '50vh' }}}>
              <TextField
                id="standard-multiline-static"
                multiline
                rows={20}
                placeholder="Enter your notes here"
                variant="outlined"
                aria-label="Enter your notes in this text area"
                sx={{backgroundColor:"white", width:"100%"}}
                />
              </Box>
            </Box>
        </Grid>
        {/* Look into this display thingy */}
        <Box sx ={{overflow: "scroll", height : { md: '50vh' }}}>
        <SceneCards/>
        </Box>

    </Grid>


    </Stack>
    </div>
  )
}

export default EditDescriptionPage;
