import * as React from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import { ButtonGroup, DialogTitle, DialogContentText } from "@mui/material";

const AskAI = () => {
  const [open, setOpen] = React.useState(false);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <React.Fragment>
      <Button variant="outlined" onClick={handleClickOpen}>
        Ask InfoBot at (add the frame)
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Ask Infobot</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To use the speech function, click on the start speaking button
          </DialogContentText>
          <TextField
            fullWidth
            multiline
            label="Your Question"
            variant="outlined"
            value={transcript}
          />
          <p>Microphone: {listening ? "on" : "off"}</p>
          <ButtonGroup
            variant="contained"
            aria-label="outlined secondary button group">
            <Button onClick={SpeechRecognition.startListening}>Start</Button>
            <Button onClick={SpeechRecognition.stopListening}>Stop</Button>
            <Button onClick={resetTranscript}>Reset Question</Button>
          </ButtonGroup>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Subscribe</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
};

export default AskAI;

