import React, { useState } from "react";
import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Box,
  Rating,
  Button,
} from "@mui/material";
import StarIcon from "@mui/icons-material/Star";
import StarBorderIcon from "@mui/icons-material/StarBorder";

const fakeUsers = [
  { id: 1, name: "User 1", description: "Description 1" },
  { id: 2, name: "User 2", description: "Description 2" },
  { id: 3, name: "User 3", description: "Description 3" },
];

const ChangeRating = () => {
  const [selectedUser, setSelectedUser] = useState(fakeUsers[0]);
  const [isRated, setIsRated] = useState(false);

  const handleUserChange = (event) => {
    const userId = event.target.value;
    const selected = fakeUsers.find((user) => user.id === userId);
    setSelectedUser(selected);
    setIsRated(false); // Reset the rating status when user changes
  };

  const handleRatingClick = () => {
    setIsRated(true);
    // You can send the rating data to your API or perform other actions here
    console.log(
      `User: ${selectedUser.name}, Description: ${selectedUser.description}, Rating: 1`
    );
  };

  return (
    <div>
      <FormControl >
        <InputLabel id="user-select-label">Select User</InputLabel>
        <Select
          labelId="user-select-label"
          id="user-select"
          value={selectedUser.id}
          onChange={handleUserChange}
        >
          {fakeUsers.map((user) => (
            <MenuItem key={user.id} value={user.id}>
              {user.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      <div style={{ marginTop: "20px" }}>
        {!isRated && (
          <Box
            component="span"
            onClick={handleRatingClick}
            style={{ cursor: "pointer" }}
          >
            <StarIcon color="primary.dark" fontSize="large" />
          </Box>
        )}
        {isRated && <p>Thanks for your rating!</p>}
      </div>

    </div>
  );
};

export default ChangeRating;
