import React, { useState, useEffect, useRef } from "react";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import ReplayIcon from '@mui/icons-material/Replay';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import dayjs from "dayjs";
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  Divider,
} from "@mui/material";
import { TimeField } from "@mui/x-date-pickers/TimeField";
import { Sidebar, Player, Navbar, Notes } from "./";
import axios from "axios";
import Cookies from "js-cookie";
import formatTime from "../utils/functions";
import { useLocation, Link } from "react-router-dom";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";



const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [endTime, setEndTime] = useState(dayjs("00:00", "mm:ss"));
  const [startTime, setStartTime] = useState(dayjs("00:00", "mm:ss"));
  const [played, setPlayed] = useState(0);
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");
  const [selectedFrameIndex, setSelectedFrameIndex] = useState(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const playerRef = useRef(null);
  const framesContainerRef = useRef(null);
  const location = useLocation();
  const { video_id, video_path } = location.state;

  function Frame({sceneNo,startTime, endTime, description }){
    const ref = useRef();

    useEffect(() => {
      if (ref.current) {
        ref.current.scrollIntoView({ behavior: "smooth", block: "end" });
      }
    }, [sceneNo]);

    return(
      <Box p={2}>
      <Grid container columnSpacing={{ xs: 1, sm: 2, md: 3 }} ref={ref}>
        {/* Displays Scene number */}
        <Grid item xs={3}>
          <Typography variant="h6" sx={{ color: "primary.dark" }}>
            Scene {sceneNo+1}
          </Typography>
        </Grid>
        {/* Displays time stamp */}
        <Grid item xs={3}>
          <Chip
            label={
              formatTime(startTime) +
              " - " +
              formatTime(endTime)
            }
            sx={{
              backgroundColor: "secondary.light",
              color: "white",
              marginBottom: "20px",
            }}
          />
        </Grid>
        <Grid item xs={6}>
          <Box display="flex" justifyContent="flex-end">
          <IconButton
              edge="end"
              aria-label="replay the scene from the start timestamp"
              onClick={() => handleReplayFrame(startTime)}
            >
              <ReplayIcon
                sx={{
                  color: "#EF6262",
                }}
                aria-label="replay the scene from the start timestamp"
              />
            </IconButton>
            <IconButton
              edge="end"
              aria-label="delete"
              onClick={() => handleRemoveFrame(sceneNo)}
              sx={{ marginLeft: "20px" }}
            >
              <DeleteIcon
                sx={{
                  color: "#EF6262",
                }}
                aria-label="delete scene"
              />
            </IconButton>
            <IconButton
              edge="end"
              aria-label="edit"
              onClick={() => handleEditFrame(sceneNo)}
              sx={{ marginLeft: "20px" }}
            >
              <EditIcon
                sx={{
                  color: "#EF6262",
                }}
                aria-label="edit scene"
              />
            </IconButton>
          </Box>
        </Grid>

        <Grid item xs={3} md={3}>
          <Typography
            variant="subtitle1"
            sx={{ color: "primary.dark" }}
          >
            Description
          </Typography>
        </Grid>
        <Grid item xs={9} md={9}>
          <Box
            p={1.5}
            borderRadius="5px"
            sx={{ backgroundColor: "white" }}
          >
            <Typography variant="body1" sx={{ color: "#1D5B79" }}>
              {description}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Box>
    );

  }

  // Load frames from localStorage on component mount
  useEffect(() => {
    const storedFrames = localStorage.getItem(`frames_${video_id}`);
    if (storedFrames) {
      setFrames(JSON.parse(storedFrames));
    }
  }, [video_id]);

  // Update frames in localStorage whenever frames state changes
  useEffect(() => {
    localStorage.setItem(`frames_${video_id}`, JSON.stringify(frames));
  }, [frames, video_id]);


  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };



  const handleAddFrame = () => {
    // Set the index of the newly added frame
    if (description) {
      const newFrame = {
        start: played,
        end: parseInt(endTime.$m) * 60 + parseInt(endTime.$s),
        description: description,
      };
      setFrames([...frames, newFrame]);
      setDescription("");
      setEndTime(dayjs("00:00", "mm:ss"));
      //console.log(parseInt(endTime.$m)*60 + parseInt(endTime.$s))
      // Find the index of the newly added frame in the sorted frames
    } else {
      // Handle invalid input (show error message, prevent adding frame, etc.)
      console.log("Invalid input. Please enter a valid end time.");
    }
  };


  const handleReplayFrame = (startTimestamp) => {
    // Call the seekToTimestamp function in the Player component
    if (playerRef.current) {
      playerRef.current.seekTo(startTimestamp, 'seconds');
      setPlayed(startTimestamp); // Update the played state to reflect the new timestamp
    }
  };

  const handleRemoveFrame = (index) => {
    const updatedFrames = [...frames];
    updatedFrames.splice(index, 1);
    setFrames(updatedFrames);
  };

  const handleEditFrame = (index) => {
    setSelectedFrameIndex(index);
    setDescription(frames[index].description);
    setEndTime(dayjs(formatTime(frames[index].end), "mm:ss"));
    // setPlayed(frames[index].time);
    setEditDialogOpen(true);
  };

  const handleSaveEdit = () => {
    if (description) {
      const updatedFrames = [...frames];
      updatedFrames[selectedFrameIndex].description = description;
      updatedFrames[selectedFrameIndex].end =
        parseInt(endTime.$m) * 60 + parseInt(endTime.$s);
      setFrames(updatedFrames);
      setEditDialogOpen(false);
      setDescription("");
      setEndTime(dayjs("00:00", "mm:ss"));
    }
  };

  const handleIncreaseEndTime = () => {
    setEndTime((prevEndTime) => prevEndTime.add(1, 'second'));
  };

  const handleDecreaseEndTime = () => {
    setEndTime((prevEndTime) => prevEndTime.subtract(1, 'second'));
  };


  const handlePublishDescriptions = async () => {
    try {
      for (let frame of frames) {
        // Assuming you have an API endpoint for posting descriptions, adjust the URL accordingly
        const apiUrl = "http://127.0.0.1:8000/descriptions/";
        const token = Cookies.get("jwtToken");
        const response = await axios.post(apiUrl, {
          video_id: video_id,
          jwt: token,
          time_stamp_start: frame.start,
          descriptions: frame.description,
          time_stamp_end: frame.end,
          ai_or_human: "human",
        });

        // Handle the response from the backend API if needed
        console.log("Description published:", response.data);
      }

      // Optional: Show a success message to the user after all descriptions are published
      console.log("All descriptions published successfully!");
    } catch (error) {
      // Handle errors if the API request fails
      console.error("Error publishing descriptions:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "90vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Grid
          container
          p={2}
          sx={{ overflowY: "auto", height: "90vh", flex: 2 }}
        >
          <Grid item xs={12} md={7}>
            <Player path={video_path} parentCallback={handleCallback} seekToTimestamp={handleReplayFrame} playerRef={playerRef} />
            <Box
              p={2}
              sx={{
                backgroundColor: "primary.main",
                borderRadius: "5px",
                paddingBottom: "20px",
              }}
              mt={1}
            >
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                Add description at {formatTime(played)}
              </Typography>
              <hr />
              <LocalizationProvider dateAdapter={AdapterDayjs}>
                <TimeField
                  label="End timestamp"
                  value={endTime}
                  onChange={(newValue) => setEndTime(newValue)}
                  format="mm:ss"
                  sx={{
                    marginBottom: "10px",
                    backgroundColor: "white",
                    borderRadius: "5px",
                  }}
                />
                        <IconButton
                  aria-label="increase end time"
                  onClick={handleIncreaseEndTime}
                >
                  <ArrowUpwardIcon />
                </IconButton>
                <IconButton
                  aria-label="decrease end time"
                  onClick={handleDecreaseEndTime}
                >
                  <ArrowDownwardIcon />
                </IconButton>
                {/* {console.log("this is value", endTime.$m, endTime.$s)} */}
              </LocalizationProvider>

              <TextField
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variant="outlined"
                fullWidth
                placeholder="Enter description"
                sx={{
                  marginBottom: "10px",
                  backgroundColor: "white",
                  borderRadius: "5px",
                }}
              />
              <Button
                sx={{ backgroundColor: "secondary.main", color: "white" }}
                className="category-btn"
                onClick={handleAddFrame}
              >
                Add Description
              </Button>
            </Box>
          </Grid>

          <Grid
            item
            xs={12}
            md={4.9}
            marginLeft={1}
            sx={{
              backgroundColor: "primary.main",
              borderRadius: "5px",
              height: "85vh", // Set the height to 75vh
              overflowY: "auto", // Set overflow to auto for vertical scrolling
            }}
          >
            <Box sx={{ height: "75vh", overflowY: "auto"}}>
              {frames
                .slice() // Create a shallow copy of the frames array
                .sort((a, b) => a.start - b.start) // Sort the frames based on starting timestamp
                .map((frame, index) => (
                    <Frame
                      key={index}
                      sceneNo={index}
                      description={frame.description}
                      startTime={frame.start}
                      endTime={frame.end}
                    />
                ))}
            </Box>

            <Dialog
              open={editDialogOpen}
              onClose={() => setEditDialogOpen(false)}
              fullWidth
            >
              <DialogTitle>Edit Description at timestamp {played}</DialogTitle>
              <DialogContent>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                  <TimeField
                    label="End timestamp"
                    value={endTime}
                    onChange={(newValue) => setEndTime(newValue)}
                    format="mm:ss"
                    sx={{
                      marginBottom: "10px",
                      backgroundColor: "white",
                      borderRadius: "5px",
                    }}
                  />
                  {/* {console.log("this is value", endTime.$m, endTime.$s)} */}
                </LocalizationProvider>
                <TextField
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  variant="outlined"
                  fullWidth
                  placeholder="Enter description"
                  style={{ marginBottom: "10px" }}
                />
              </DialogContent>
              <DialogActions>
                <Button
                  onClick={() => setEditDialogOpen(false)}
                  sx={{ backgroundColor: "secondary.main", color: "white" }}
                  className="category-btn"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleSaveEdit}
                  sx={{ backgroundColor: "secondary.main", color: "white" }}
                  className="category-btn"
                >
                  Save
                </Button>
              </DialogActions>
            </Dialog>
            <Divider />
                <Button
                  sx={{
                    backgroundColor: "secondary.main",
                    color: "white",
                    margin:"20px"
                  }}

                  className="category-btn"
                  onClick={handlePublishDescriptions}
                  disabled={frames.length <= 0}
                >
                  Publish Description
                </Button>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default AddDescriptions;



