import {React , useState} from 'react';
import ReactPlayer from 'react-player'
// import video from './videos/18.mp4'
const Player = (props) => {
    // import video from './videos/18.mp4'
    const [played, setPlayed] = useState(0);
    const video = require('./' + props.path)
    return(
    <div>
    {played}
    <ReactPlayer 
    url={video}
    playing={true}
    controls={true} 
    width={"100%"}
    onProgress={(progress) => {
       setPlayed(Math.floor(progress.playedSeconds));
     }}
    />
    </div>
    )
}

export default Player;
