import React, { useState, useEffect } from "react";
import { Stack, Typography, Button } from "@mui/material";
import { Link } from "react-router-dom";
import Cookies from "js-cookie";
import { logo } from "../utils/constants";
import SearchBar from "./SearchBar";

const Navbar = () => {
  const isLoggedIn = Cookies.get("jwtToken");
  const [isTabPressed, setTabPressed] = React.useState(false);
  const [isTabPressedOnce, setTabPressedOnce] = React.useState(false);

  React.useEffect(() => {
    const handleTabPress = (event) => {
      console.log("coming insode")
      if (event.key === "Tab" && !isTabPressedOnce) {
        console.log("coming insode hi there")
        setTabPressed(true);
        setTabPressedOnce(true);
      }
      if (event.key === "Tab" && isTabPressedOnce) {
        console.log("coming insode yolo")
        setTabPressed(false);
      }

    };

    window.addEventListener("keydown", handleTabPress);

    return () => {
      window.removeEventListener("keydown", handleTabPress);
    };
  }, []);

  const logout = () => {
    // Clear the JWT token from cookies
    Cookies.remove("jwtToken");
    window.location.reload();
  };

  return (
    <div>
      {isTabPressed && (
        <Button
          component={Link}
          to="#main-content"
          sx={{
            backgroundColor: "primary.main",
            color: "white",
          }}
          className="jump-to-content-btn"
        >
          Jump to Content
        </Button>
      )}

      <Stack
        direction="row"
        alignItems="center"
        p={2}
        sx={{
          position: "sticky",
          background: "#D9D9D9",
          top: 0,
          justifyContent: "space-between",
        }}
      >
        {/* This displays the logo */}
        <Link to="/" style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="logo" height={45} />
        </Link>

        <SearchBar />

        {isLoggedIn ? (
          <Button
            sx={{
              backgroundColor: "secondary.main",
              color: "white",
            }}
            className="category-btn"
            onClick={logout}
          >
            Sign Out
          </Button>
        ) : (
          <Link to={"/SignIn"}>
            <Button
              sx={{
                backgroundColor: "secondary.main",
                color: "white",
              }}
              className="category-btn"
            >
              Sign In
            </Button>
          </Link>
        )}
      </Stack>
    </div>
  );
};

export default Navbar;

