import React, { useState } from "react";
import { Sidebar, Navbar } from "./";
import {
  Box,
  Stack,
  Typography,
  Button,
  CircularProgress,
  FormControl,
  RadioGroup,
  FormControlLabel,
  Radio,
  TextField,
} from "@mui/material";
import FileUploadIcon from "@mui/icons-material/FileUpload";
import axios from "axios";
import Cookies from "js-cookie";

const UploadVideo = () => {
  const [selectedCategory, setSelectedCategory] = useState("Upload");
  const [loading, setLoading] = useState(false);
  const [heading, setHeading] = useState("Upload a video file");
  const [smallHeading, setSmallHeading] = useState(
    "Once uploaded and described, your video will be visible on homepage"
  );
  const [visibility, setVisibility] = useState("private");
  const [selectedFile, setSelectedFile] = useState(null);
  const [videoTitle, setVideoTitle] = useState("");

  const handleVisibilityChange = (event) => {
    setVisibility(event.target.value);
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    handleFileUpload()
  };

  const handleTitleChange = (event) => {
    setVideoTitle(event.target.value);
  };

  const handleFileUpload = () => {
    if (!videoTitle.trim()) {
      alert("Please enter a video title.");
      return;
    }

    if (!selectedFile) {
      alert("Please select a video file.");
      return;
    }

    console.log("I am coming here")
    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("title", videoTitle);
    formData.append("visibility", visibility);

    const token = Cookies.get("jwtToken");
    const apiUrl = "http://127.0.0.1:8000/api/upload/";

    axios
      .post(apiUrl, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        // Handle the successful response
        console.log("Response:", response.data);
        setHeading("Your file is being loaded, do not refresh the page");
        setSmallHeading(
          "You will be redirected to the video page when the video is described"
        );
        setLoading(true);
      })
      .catch((error) => {
        // Handle errors, if any
        console.error("Error:", error);
        setHeading("Error in uploading your file, please try again");
        setSmallHeading("Make sure you are uploading an mp4 file");
      });
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Stack
          spacing={2}
          paddingTop={15}
          width={"100%"}
          justifyContent={"flex-start"}
          alignItems={"center"}
        >
          <Typography
            variant="h4"
            fontWeight="bold"
            paddingTop={3}
            color="primary.dark"
          >
            {heading}
          </Typography>
          <Typography variant="caption" fontWeight="bold" color="primary.dark">
            {smallHeading}
          </Typography>
          <Typography
            variant="caption"
            fontWeight="bold"
            color="secondary.main"
          >
            Choose whether you would like the video to be public or private. The
            video is private by default.
          </Typography>
          <FormControl component="fieldset">
            <RadioGroup
              row
              aria-label="visibility"
              name="visibility"
              value={visibility}
              onChange={handleVisibilityChange}
            >
              <FormControlLabel
                value="private"
                control={<Radio />}
                label="Private"
              />
              <FormControlLabel
                value="public"
                control={<Radio />}
                label="Public"
              />
            </RadioGroup>
          </FormControl>

          <TextField
            label="Video Title"
            variant="outlined"
            sx={{ width: "50%" }}
            margin="normal"
            value={videoTitle}
            onChange={handleTitleChange}
          />

          {!loading && (
            <label htmlFor="fileInput">
              <Button
                component="span"
                sx={{ backgroundColor: "secondary.main", color: "white" }}
                className="category-btn"
                startIcon={<FileUploadIcon />}
              >
                Upload Video File
              </Button>
              <input
                type="file"
                accept=".mp4"
                id="fileInput"
                style={{ display: "none" }}
                onChange={handleFileUpload}
              />
            </label>
          )}

          {loading && <CircularProgress />}
        </Stack>
      </Stack>
    </div>
  );
};

export default UploadVideo;

