import { Stack } from '@mui/material';

import { categories } from '../utils/constants';

const selectedCategory = "Home";

const Sidebar = () => (
    <Stack
        direction= "row"
        sx = {{
            overflowY: "auto",
            height : { sx: 'auto', md: '95%' },
            flexDirection : { md : 'column'},
            px : { md : 2}
        }}
        style={ {backgroundColor : '#1D5B79'}}>

        {categories.map((category) => (
            <button className='category-btn'
            style={{
                background: category.name === selectedCategory && '#EF6262',
                color: 'white',
            }}
            key = {category.name}
            >
                <span
                style={{
                color: category.name === selectedCategory ? 'white' : '#EF6262',
                color: 'white',
                }}>{category.icon}</span>
                <span>{category.name}</span>
            </button>
        ))}

    </Stack>
  )

export default Sidebar
