import {React , useState} from 'react';
import ReactPlayer from 'react-player'

const Player = (props) => {
    const [played, setPlayed] = useState(0);
    const [isPlaying, setIsPlaying] = useState(true);
    const video = require('./' + props.path)
    //pauses the video once it is played 
    return(
    <div>
    <ReactPlayer 
    url={video}
    playing={props.pauseOrPlay}
    controls={true} 
    width={"100%"}
    height={"65vh"}
    onProgress={(progress) => {
       setPlayed(Math.floor(progress.playedSeconds));
       props.parentCallback(Math.floor(played/60) + ":" + played%60);
     }}
    />
    </div>
    )
}

export default Player;
