import React, { useState } from 'react';
import { FormControl, InputLabel, Select, MenuItem, TextField, Box, Rating, Button } from '@mui/material';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';

const fakeUsers = [
  { id: 1, name: 'User 1', description: 'Description 1' },
  { id: 2, name: 'User 2', description: 'Description 2' },
  { id: 3, name: 'User 3', description: 'Description 3' },
];

const ChangeRating = () => {
    const [selectedUser, setSelectedUser] = useState(fakeUsers[0]);
    const [rating, setRating] = useState(0);

    const handleUserChange = (event) => {
      const userId = event.target.value;
      const selected = fakeUsers.find((user) => user.id === userId);
      setSelectedUser(selected);
    };

    const handleRatingChange = (value) => {
      setRating(value);
    };

    const handleSubmitRating = () => {
      // You can send the rating data to your API or perform other actions here
      console.log(`User: ${selectedUser.name}, Description: ${selectedUser.description}, Rating: ${rating}`);
    };

    return (
      <div>
        <FormControl fullWidth>
          <InputLabel id="user-select-label">Select User</InputLabel>
          <Select
            labelId="user-select-label"
            id="user-select"
            value={selectedUser.id}
            onChange={handleUserChange}
          >
            {fakeUsers.map((user) => (
              <MenuItem key={user.id} value={user.id}>
                {user.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <div style={{ marginTop: '20px' }}>
          <TextField
            label="Current Description"
            variant="outlined"
            fullWidth
            multiline
            value={selectedUser.description}
            disabled
          />
        </div>

        <div style={{ marginTop: '20px' }}>
          <Box component="fieldset" borderColor="transparent">
            <Rating
              name="rating"
              value={rating}
              onChange={(event, newValue) => handleRatingChange(newValue)}
              emptyIcon={<StarBorderIcon fontSize="inherit" />}
              icon={<StarIcon fontSize="inherit" />}
            />
          </Box>
        </div>

        <div style={{ marginTop: '20px' }}>
          <Button variant="contained" color="primary" onClick={handleSubmitRating}>
            Submit Rating
          </Button>
        </div>
      </div>
    );
};

export default ChangeRating;
