import axios from "axios";
import Cookies from "js-cookie";

// this functions retrieves the descriptions for a specfic video id
export const getDescriptions = (video_id) => {
  const apiUrl = "http://127.0.0.1:8000/descriptions/";
  const token = Cookies.get("jwtToken");

  const params = {
    video_id: video_id,
    jwt: token,
  };

  axios
    .get(apiUrl, {
      params: params,
    })
    .then((response) => {
        const descriptions = response.data.descriptions.map((item) => {
          return JSON.parse(item);
        })
        console.log(
            "This is what I am recieving in functions.js",
            descriptions
          );
        return descriptions
    })
    .catch((err) => {
      console.log("Not recieving data", err);
    });
};
