import { Typography, Grid } from '@mui/material'
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import Cookies from "js-cookie";

const Comment = (props) => {
  const apiUrl = "http://127.0.0.1:8000/api/ask_question/";
  const token = Cookies.get("jwtToken");
  const [comments, setComments] = useState();
  const [isLoading, setLoading] = useState(true);

  const params = {
    jwt: token,
    // id: props.videoID,
    id: 18,
  };

  useEffect(() => {
    axios
      .get(apiUrl, {
        params: params,
      })
      .then((response) => {
        // console.log(
        //   "This is what I am recieving in comments page",
        //   response.data
        // );
        // if (response.data.questions !== "NOT_FOUND")
        // { 
        //   setComments(
        //     response.data.questions.map((item) => {
        //       return JSON.parse(item);
        //     })
        //   );
        // }

        setLoading(false);
        console.log(
          "This is what I am recieving in comments page",
          response.data.questions
        );
      })
      .catch((err) => {
        console.log("Not recieving data in comments", err);
      });
  }, []);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  return (
    <Grid container p={2}>
    {/* This grid displays username */}
    <Grid item xs={8} md={10}>
       <Typography variant="subtitle2" sx={{color : "#468B97"}}>{props.username}</Typography>
    </Grid>

    {/* this grid displays timestamp */}
    <Grid item xs={4} md={2}>
            <Typography variant="subtitle2" color="white" backgroundColor="#EF6262" padding="3px" borderRadius="5px">
            {props.timeStamp}
            </Typography>
    </Grid>
    {/* This grid displays the question and answer */}
    <Grid item xs={12}>
       <Typography variant="subtitle2" sx={{color : "primary.dark"}}>Question:  {props.question}</Typography>
       <Typography variant="subtitle2" sx={{color : "primary.dark"}} >Answer: {props.answer}</Typography>
    </Grid>
</Grid>
  )
}

export default Comment;
