import React, { useState, useEffect } from "react";
import { Button } from "@mui/material";

const TextToSpeech = ({ text, videoRef }) => {
  const [isPaused, setIsPaused] = useState(false);
  const [utterance, setUtterance] = useState(null);
  const [voice, setVoice] = useState(null);
  const [pitch, setPitch] = useState(1);
  const [rate, setRate] = useState(1);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    const synth = window.speechSynthesis;
    const u = new SpeechSynthesisUtterance(text);
    const voices = synth.getVoices();

    setUtterance(u);
    setVoice(voices[0]);

    return () => {
      synth.cancel();
    };
  }, [text]);

  useEffect(() => {
    const synth = window.speechSynthesis;

    const handleVideoTimeUpdate = () => {
      // Adjust this value based on your video and description synchronization requirements
      const descriptionStartTime = 5; // Description starts playing when the video reaches 5 seconds

      if (videoRef.current.currentTime >= descriptionStartTime && !synth.speaking && !isPaused) {
        utterance.voice = voice;
        utterance.pitch = pitch;
        utterance.rate = rate;
        utterance.volume = volume;
        synth.speak(utterance);
      }
    };

    // Attach event listener for video time update
    videoRef.current.addEventListener("timeupdate", handleVideoTimeUpdate);

    return () => {
      // Remove event listener on component unmount
      videoRef.current.removeEventListener("timeupdate", handleVideoTimeUpdate);
    };
  }, [utterance, voice, pitch, rate, volume, isPaused, videoRef]);

  const handlePlay = () => {
    const synth = window.speechSynthesis;

    if (isPaused) {
      synth.resume();
    } else {
      synth.speak(utterance);
    }

    setIsPaused(false);
  };

  const handlePause = () => {
    const synth = window.speechSynthesis;

    synth.pause();

    setIsPaused(true);
  };

  const handleStop = () => {
    const synth = window.speechSynthesis;

    synth.cancel();

    setIsPaused(false);
  };

  const handleVoiceChange = (event) => {
    const voices = window.speechSynthesis.getVoices();
    setVoice(voices.find((v) => v.name === event.target.value));
  };

  const handleRateChange = (event) => {
    setRate(parseFloat(event.target.value));
  };

  const handleVolumeChange = (event) => {
    setVolume(parseFloat(event.target.value));
  };

  return (
    <div>
      <label>
        Voice:
        <select value={voice?.name} onChange={handleVoiceChange}>
          {window.speechSynthesis.getVoices().map((voice) => (
            <option key={voice.name} value={voice.name}>
              {voice.name}
            </option>
          ))}
        </select>
      </label>

      <br />

      <label>
        Speed:
        <input
          type="range"
          min="0.5"
          max="2"
          step="0.1"
          value={rate}
          onChange={handleRateChange}
        />
      </label>
      <br />
      <label>
        Volume:
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={handleVolumeChange}
        />
      </label>

      <br />

      <Button sx= {{backgroundColor: "secondary.main", color : "white", margin:"10px"}} className="category-btn" onClick={handlePlay}>{isPaused ? "Resume" : "Play"}</Button>
      <Button sx= {{backgroundColor: "secondary.main", color : "white", margin:"10px"}} className="category-btn" onClick={handlePause}>Pause</Button>
      <Button sx= {{backgroundColor: "secondary.main", color : "white",  margin:"10px"}} className="category-btn" onClick={handleStop}>Stop</Button>
    </div>
  );
};

export default TextToSpeech;
