import React, { useState } from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import {
  Box,
  Stack,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
} from "@mui/material";
import { Sidebar, Player, Navbar, Notes } from "./";

const AddDescriptions = () => {
  const [selectedCategory, setSelectedCategory] = useState();
  const [played, setPlayed] = useState();
  const [frames, setFrames] = useState([]);
  const [description, setDescription] = useState("");
  const [selectedFrameIndex, setSelectedFrameIndex] = useState(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleAddFrame = () => {
    if (played) {
      const newFrame = { time: played, description: description };
      setFrames([...frames, newFrame]);
      setDescription("");
    }
  };

  const handleRemoveFrame = (index) => {
    const updatedFrames = [...frames];
    updatedFrames.splice(index, 1);
    setFrames(updatedFrames);
  };

  const handleEditFrame = (index) => {
    setSelectedFrameIndex(index);
    setDescription(frames[index].description);
    setEditDialogOpen(true);
  };

  const handleSaveEdit = () => {
    const updatedFrames = [...frames];
    updatedFrames[selectedFrameIndex].description = description;
    setFrames(updatedFrames);
    setEditDialogOpen(false);
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box
          sx={{
            height: { sx: "auto", md: "100vh" },
            paddingRight: { sx: 0, md: 2 },
          }}
        >
          <Sidebar
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
          />
        </Box>

        <Grid container spacing={2} p={3} sx={{ overflowY: "auto", height: "90vh", flex: 2 }}>
          <Grid item xs={12} md={8}>
            <Player path={"videos/19.mp4"} parentCallback={handleCallback} />
          </Grid>

          <Notes videoId={19} />

          <Grid item xs={12} md={8}>
            <Box p={2} sx={{ backgroundColor: "primary.main", borderRadius: "5px", paddingBottom: "20px" }}>
              <Typography variant="h6" sx={{ color: "primary.dark" }}>
                Add Frame at {played}
              </Typography>
              <hr />
              <TextField
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                variant="outlined"
                fullWidth
                placeholder="Enter description"
                sx={{ marginBottom: "10px", backgroundColor: "white", borderRadius: "5px" }}
              />
              <Button  sx={{ backgroundColor: "secondary.main", color: "white"}} className="category-btn" onClick={handleAddFrame}>
                Add Frame
              </Button>
            </Box>

            {frames.map((frame, index) => (
            <Box  p={2} sx= {{ backgroundColor : "#1D5B79" , borderRadius : "5px", marginBottom : "32px"}} >
      <Grid container rowSpacing={2} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
        {/* Displays Scene number */}
        <Grid item xs={2}>
          <Typography variant="h6" sx={{color : "white"}} >Scene {index}</Typography>
        </Grid>
        {/* Displays time stamp */}
        <Grid item xs={1} >
        <Chip
                label={description.timestamp}
                sx={{
                  backgroundColor: "secondary.light",
                  color: "white",
                  marginBottom: "20px",
                }}
              />
        </Grid>
        <Grid item xs={9}>
        <Box display="flex" justifyContent="flex-end">
        <span onClick={handleDelete}>
        <DeleteIcon
          sx={{ 
          color: '#EF6262' }} 
          aria-label='delete scene'
          />
        </span>
        </Box>
        </Grid>

        <Grid item xs={3} md={2}>
          <Typography variant="subtitle1" sx={{color : "white"}}>On screen text</Typography>
        </Grid>
        <Grid item xs={9} md={10}>
        <Box p={1.5}  borderRadius="5px" sx={{backgroundColor : "white"}}>
          <Typography variant="body1" sx={{color : "#1D5B79"}} > 
            {props.screenText}
          </Typography>
        </Box>
        </Grid>

        <Grid item xs={3} md={2}>
          <Typography variant="subtitle1" sx={{color : "white"}} >Description</Typography>
        </Grid>
        <Grid item xs={9} md={10}>
        <Box p={1.5}  borderRadius="5px" sx={{backgroundColor : "white"}}>
          <Typography variant="body1" sx={{color : "#1D5B79"}} > 
            {props.description}
          </Typography>
        </Box>
        </Grid>

      </Grid>
    </Box>
    ))}


            <List>
              {frames.map((frame, index) => (
                <ListItem key={index}>
                  <ListItemText primary={`Time: ${frame.time}`} secondary={`Description: ${frame.description}`} />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="edit" onClick={() => handleEditFrame(index)}>
                      Edit
                    </IconButton>
                    <IconButton edge="end" aria-label="delete" onClick={() => handleRemoveFrame(index)}>
                      Delete
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>

            <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
              <DialogTitle>Edit Description</DialogTitle>
              <DialogContent>
                <TextField
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  variant="outlined"
                  fullWidth
                  placeholder="Enter description"
                  style={{ marginBottom: "10px" }}
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setEditDialogOpen(false)} color="primary">
                  Cancel
                </Button>
                <Button onClick={handleSaveEdit} color="primary">
                  Save
                </Button>
              </DialogActions>
            </Dialog>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default AddDescriptions;
