import React, { useState, useEffect } from "react";
import {
  Box,
  Stack,
  Typography,
  Grid,
  Button,
} from "@mui/material";
import { Sidebar, Comment, AskAI, Navbar, VideoPlayer, ChangeRating } from "./";
import { useLocation, Link } from "react-router-dom";
import axios from "axios";
import Cookies from "js-cookie";
import formatTime from  '../utils/functions'

const VideoPage = () => {
  const [isLoading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [open, setOpen] = useState(false);
  const [played, setPlayed] = useState(0);
  const [desc, setDesc] = useState();
  const [descUser, setDescUser] = useState();
  const [video, setVideo] = useState([]);

  // these are some error handling checks that I am doing
  const [noDescription, setNoDescription] = useState(false);
  const [noComments, setNoComments] = useState(false);

  const location = useLocation();
  const { video_id, path } = location.state;

  const apiUrl = "http://127.0.0.1:8000/api/video/";
  const token = Cookies.get("jwtToken");

  const params = { id: video_id};
  const url = "http://127.0.0.1:8000/descriptions/";
  const parameters = { video_id: video_id};

  useEffect(() => {
    axios.get(apiUrl, { params: params })
      .then((response) => {
        setVideo(response.data.video);
        console.log("Received video data", response.data.video);
      })
      .catch((err) => {
        console.log(err);
      });

    axios.get(url, { params: parameters })
      .then((response) => {
        console.log("Received description data", response.data.descriptions);
        const descriptions = response.data.descriptions.map((item) => {return JSON.parse(item);})
        setDesc(descriptions);
        // this just gets the usernames for all the user and then display the descriptions from only one user
        const uniqueUsernames = Array.from(
          new Set(descriptions.map((description) => description.username))
        );
        setDescUser(
          descriptions.filter(
            (description) => description.username === uniqueUsernames[0]
        ));
        setLoading(false);
      })
      .catch((err) => {
        console.log("Not receiving data description data", err);
        setLoading(false);
      });

  }, []);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleCallback = (progressData) => {
    setPlayed(progressData);
  };

  const handleChangeUserCallback = (filteredDescriptions) => {
    setDescUser(filteredDescriptions);
  };

  return (
    <div>
      <Navbar />
      <Stack sx={{ flexDirection: { sx: "column", md: "row" } }}>
        <Box sx={{ height: { sx: "auto", md: "100vh" } }}>
          <Sidebar selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />
        </Box>

        <Grid container spacing={2} p={3} sx={{ overflowY: "auto", height: "90vh", flex: 2 }}>
          <Grid item xs={12} md={8}>
            {!isLoading && <VideoPlayer path={video.video_path} title={video.title} descrip={descUser} parentCallback={handleCallback} />}
            <ChangeRating descriptions={desc} parentCallback={handleChangeUserCallback} />
          </Grid>

          {/* This grid is to showcase all the comments */}
          <Grid item xs={6} md={4}>
            <Box p={2} sx={{ backgroundColor: "primary.main", borderRadius: "5px", paddingBottom: "20px" }}>
              <Typography variant="h6" sx={{ color: "primary.dark" }}>InfoBot Q&A</Typography>
              <hr />
              <Box sx={{ overflow: "scroll", height: { md: "55vh" } }}>
              <Comment videoID={video_id}/>
              </Box>
            </Box>

            {open && <AskAI videoID={video_id} timeStamp={played} parentCallback={handleClose} />}

            <Box p={2} sx={{ backgroundColor: "primary.main", borderRadius: "5px", margin: "20px 0" }}>
              <Typography variant="h6" sx={{ color: "primary.dark" }}>More Options</Typography>
              <hr />
              <Button sx={{ backgroundColor: "secondary.main", color: "white", width: "100%", marginTop: "10px" }} className="category-btn" onClick={handleClickOpen}>Ask infobot at {formatTime(played)}</Button>
              <Link to="/AddDescriptions" state={{ video_id: video_id, video_path: video.video_path }}>
                <Button sx={{ backgroundColor: "secondary.main", color: "white", width: "100%", marginTop: "10px" }} className="category-btn" >Add description</Button>
              </Link>
              <Link to="/EditDescriptions" state={{ video_id: video_id, video_path: video.video_path }}>
              <Button sx={{ backgroundColor: "secondary.main", color: "white", width: "100%", marginTop: "10px" }} className="category-btn" >Edit descriptions</Button>
              </Link>
            </Box>
          </Grid>
        </Grid>
      </Stack>
    </div>
  );
};

export default VideoPage;




